mapper.delete:
/* RoleAuthDao.deleteAuthMenuMpp */
DELETE FROM TB_ROLE_MENU WHERE ROLEID = #{roleid}

 AND MENUID = #{menuid}

mapper.select:
/* RoleAuthDao.selectRoleTreeSitenm */
SELECT
SITE_NM
FROM TB_SITE
WHERE SITEID = #{siteid}

/* RoleAuthDao.selectRoleTreeList */

    SELECT
        IFNULL(UPPR_ROLEID , '') pkey,
        A.ROLEID 'key',  
        A.NM  title
    FROM TB_ROLE A
    WHERE 1=1


    AND A.SE_CD = #{se_cd}


    AND A.USE_YN = #{role_filter}

ORDER BY ORD

/* RoleauthDao.selectRoleInfo */
   SELECT 
       ROLEID, UPPR_ROLEID, NM, DC, USE_YN, ORD
   FROM TB_ROLE A
   WHERE ROLEID = #{roleid}

mapper.insert:
/* RoleAuthDao.insertRoleInfo */
INSERT INTO
    TB_ROLE( NM, SE_CD, UPPR_ROLEID, DC, USE_YN, ORD, UPDT_DT, UPDTUSERID, REG_DT, REGUSERID)
    VALUES ( #{nm}, #{se_cd}, #{uppr_roleid}, #{dc}, #{use_yn},
        (SELECT IFNULL(MAX(ORD),0)+1 FROM TB_ROLE A WHERE A.UPPR_ROLEID = #{uppr_roleid}),
        NOW(),
        #{reguserid},
        NOW(),
        #{reguserid}
    )

mapper.select:
/* RoleAuthDao.selectRoleUserMappedList */
SELECT RU.ROLEID, R.NM AS ROLE_NM, RU.USERID, RU.ROLE_STRT_DT, RU.ROLE_END_DT, RU.UPDT_DT, U.ACNT, U.NM, U.USER_SE_CD FROM TB_ROLE_USER RU INNER JOIN TB_USER U ON RU.USERID = U.USERID INNER JOIN TB_ROLE R ON RU.ROLEID = R.ROLEID WHERE 1=1
AND ${searchType} LIKE CONCAT('%',#{searchKeyword},'%')
AND RU.ROLEID = #{roleid}

/* RoleAuthDao.selectRoleMenuTreeList */
SELECT B.pkey, B.MENUID as 'key', B.TITLE as title, IFNULL(C.EXT1,'N') as ext1 FROM (SELECT IFNULL(A.UPPR_MENUID, 0) pkey, A.MENUID, A.NM TITLE, A.ORD FROM TB_MENU A
WHERE 1=1
AND SITEID = #{siteid}
) B LEFT OUTER JOIN (SELECT AA.MENUID, 'Y' EXT1 FROM TB_ROLE_MENU AA WHERE AA.ROLEID = #{roleid} ) C ON B.MENUID = C.MENUID ORDER BY B.ORD ASC

mapper.update:
        /* RoleAuthDao.updateRoleAuth */
        UPDATE TB_ROLE SET
            UPDTUSERID = #{updtuserid},
            UPDT_DT = NOW()

	            ,SE_CD = #{se_cd}

        WHERE ROLEID IN (
            SELECT * FROM (
                SELECT ROLEID FROM
                    TB_ROLE
                WHERE ROLEID = #{roleid}
                ) A
            )

/* RoleAuthDao.updateRoleUser */
UPDATE 
    TB_ROLE_USER 
SET
    ROLE_STRT_DT = #{role_strt_dt}, 
    ROLE_END_DT = #{role_end_dt}, 
    UPDT_DT = NOW(),
    UPDTUSERID = #{reguserid} 
WHERE 
    ROLEID = #{roleid} AND USERID = #{userid}
