mapper.select:
/* FileDao.getFileList */ SELECT FILEID, FILEGRPID, FILE_IDNTFC_KEY, FILE_PATH, SAVE_FILE_NM, ORGINL_FILE_NM, FILE_EXTSN, FILE_SIZE, ORD, REG_DT, REGUSERID FROM TB_FILE WHERE 1=1
AND FILEGRPID = #{filegrpid}
ORDER BY ORD

mapper.delete:
/* FileDao.deleteFile */
DELETE
FROM TB_FILE
WHERE FILEID = #{fileid}
  AND FILE_IDNTFC_KEY = #{fileIdntfcKey}
