choose.when:
AND (${tableAlias}.INSTID IN( SELECT INSTID FROM TB_CMM_INST WHERE USE_YN = 'Y' AND APRV_STTS_CD = '2'
AND INSTID = #{user.instid}
AND INSTID = #{user.instid}
AND INSTID IN(SELECT INSTID FROM TB_CMM_ROLE_INST WHERE ROLEID = #{user.roleInfo.roleid})
AND 1 != 1
AND INSTID IN(SELECT INSTID FROM TB_CMM_INST WHERE RGN_CD IN(SELECT SIGNGU_CD FROM TB_CMM_ADDR_SIGNGU WHERE CTPRVN_CD IN (SELECT RGN_CD FROM TB_CMM_ROLE_RGN WHERE ROLEID = #{user.roleInfo.roleid})))
)
OR ${tableAlias}.INSTID IS NULL
)

choose.otherwise:
AND ${tableAlias}.INSTID IN(
    SELECT 
        INSTID
    FROM 
        TB_CMM_INST
    WHERE 
        USE_YN = 'Y'
    AND APRV_STTS_CD = '2'


        AND INSTID = #{searchInstValue}


        AND INSTID = #{searchInstValue}    
        AND #{searchInstValue} = #{user.instid}   


        AND INSTID = #{searchInstValue}
        AND #{searchInstValue} IN(SELECT INSTID FROM TB_CMM_ROLE_INST WHERE ROLEID = #{user.roleInfo.roleid})           


        AND 1 != 1



    AND INSTID IN(SELECT INSTID FROM TB_CMM_INST WHERE RGN_CD IN(SELECT SIGNGU_CD FROM TB_CMM_ADDR_SIGNGU WHERE CTPRVN_CD IN (SELECT RGN_CD FROM TB_CMM_ROLE_RGN WHERE ROLEID = #{user.roleInfo.roleid})))          

        )

choose.when:
