AND SYS_SE_CD = #{searchSiteSysSeCd}
AND INSTID = #{user.instid}
AND INSTID IN(SELECT INSTID FROM TB_CMM_ROLE_INST WHERE ROLEID = #{user.roleInfo.roleid})
AND 1 != 1
AND INSTID IN(SELECT INSTID FROM TB_CMM_INST WHERE RGN_CD IN(SELECT SIGNGU_CD FROM TB_CMM_ADDR_SIGNGU WHERE CTPRVN_CD IN (SELECT RGN_CD FROM TB_CMM_ROLE_RGN WHERE ROLEID = #{user.roleInfo.roleid})))
)
OR ${tableAlias}.SITEID IS NULL
)

choose.otherwise:
AND ${tableAlias}.SITEID IN(
    SELECT 
        SITEID
    FROM
        TB_CMM_SITE
    WHERE 
        USE_YN = 'Y'

    AND SYS_SE_CD = #{searchSiteSysSeCd}



        AND SITEID = #{searchSiteValue}


        AND SITEID = #{searchSiteValue}
        AND INSTID = #{user.instid}


        AND SITEID = #{searchSiteValue}
        AND INSTID IN(SELECT INSTID FROM TB_CMM_ROLE_INST WHERE ROLEID = #{user.roleInfo.roleid})          


        AND 1 != 1



    AND INSTID IN(SELECT INSTID FROM TB_CMM_INST WHERE RGN_CD IN(SELECT SIGNGU_CD FROM TB_CMM_ADDR_SIGNGU WHERE CTPRVN_CD IN (SELECT RGN_CD FROM TB_CMM_ROLE_RGN WHERE ROLEID = #{user.roleInfo.roleid})))      

        )



AND ${tableAlias}.${columnName} = #{searchCtprvnValue}
AND ${tableAlias}.${columnName} IN(SELECT RGN_CD FROM TB_CMM_ROLE_RGN WHERE ROLEID = #{user.roleInfo.roleid})     


AND ${tableAlias}.${columnName} = #{searchCtprvnValue}


)
