mapper.select:
/* BbsDao.selectPstList */
SELECT A.* FROM ( SELECT A.PSTID, A.TITLE, A.HITS, CASE WHEN FXD_NTC_YN = 'Y' THEN 1 WHEN FXD_NTC_YN = 'N' THEN 2 ELSE 3 END AS FXD_NTC_YN_ORD, FXD_NTC_YN, FXD_NTC_STRT_DT, FXD_NTC_END_DT, IFNULL(B.FILECNT,0) file_cnt, B.NM , CONVERT(A.REG_DT, CHAR(10)) REG_DT, CONVERT(A.MDFCN_DT, CHAR(10)) MDFCN_DT, A.MDFCN_DT ord_DT, A.LOGIN_YN FROM TB_CMM_PST A LEFT OUTER JOIN (SELECT FILEGRPID, COUNT(FILEID) FILECNT FROM TB_CMM_FILE GROUP BY FILEGRPID) B ON A.FILEGRPID =B.FILEGRPID INNER JOIN TB_CMM_USER B ON(A.REGUSERID = B.USERID) WHERE 1=1 AND (CASE WHEN FXD_NTC_YN ='Y' AND FXD_NTC_STRT_DT <= NOW() AND DATE_ADD(FXD_NTC_END_DT, INTERVAL 1 DAY) >= NOW() THEN 'Y' WHEN FXD_NTC_YN ='Y' AND FXD_NTC_STRT_DT = null AND FXD_NTC_END_DT = null THEN 'Y' WHEN FXD_NTC_YN ='N' THEN 'Y' ELSE 'N' END )= 'Y'
AND BBSID= #{bbsid}
AND A.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CNTNTS LIKE CONCAT('%', #{searchKeyword}, '%')
) A WHERE 1=1
AND A.NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.LOGIN_YN = 'N'

/* BbsDao.selectCmntList */
SELECT a.PSTID, a.CMNTID, a.CNTNTS, a.PRNTS_CMNTID, a.CMNT_GRP, a.DPTH, a.ORD, a.OPN_YN, a.MDFCN_DT, a.MDFRID, a.REG_DT, a.REGUSERID, a.del_yn, b.NM, b.ACNT FROM TB_CMM_CMNT a, TB_CMM_USER b WHERE 1=1 AND a.REGUSERID = b.USERID -- AND A.DEL_YN = 'N' AND PSTID = #{pstid}
AND PRNTS_CMNTID = #{prntsCmntid}
-- ORDER BY CMNT_GRP, PRNTS_CMNTID,DPTH,ORD

mapper.update:
/* BbsDao.updatePstHitsCount */
UPDATE TB_CMM_PST SET
     HITS        = IFNULL(HITS,0)+1 
    ,MDFCN_DT     = NOW()

    ,MDFRID  = #{user.userid }

WHERE 
    pstid = #{pstid}

/* BbsDao.updateCmnt */
UPDATE TB_CMM_CMNT SET
    CNTNTS        =#{cntnts}, 
    MDFCN_DT       = NOW(),
    MDFRID    =#{user.userid}
WHERE 
    cmntid = #{cmntid}

/* BbsDao.updateCmntOrdElse */
SELECT CASE WHEN (SELECT IFNULL(MAX(ORD),0) FROM TB_CMM_CMNT WHERE CMNT_GRP= #{cmntGrp} AND DPTH = #{dpth}+1 ) = 0 THEN #{ord}+1 ELSE (SELECT IFNULL(MAX(ORD),0) FROM TB_CMM_CMNT WHERE CMNT_GRP= #{cmntGrp} AND DPTH = #{dpth}+1 )+1 END
update tb_cmm_cmnt set ord = ord +1 where CMNT_GRP = #{cmntGrp} and ord >= #{ord}

/* BbsDao.updateCmntOrd */
UPDATE TB_CMM_CMNT SET
    ORD           = #{ord}, 
    MDFCN_DT       = NOW(),
    MDFRID    = #{user.userid}
WHERE 
   cmntid = #{cmntid}

/* BbsDao.updateCmntReplyDelYn */
UPDATE TB_CMM_CMNT SET
    DEL_YN      = #{delYn},
    MDFCN_DT     = NOW(),
    MDFRID  = #{user.userid }
WHERE 
    cmntid = #{cmntid}

/* BbsDao.updatePst */ UPDATE TB_CMM_PST SET TITLE = #{title }, CNTNTS = #{cntnts }, BBS_CLID = #{bbsClid }, FILEGRPID = #{filegrpid }, MDFCN_DT = NOW(), MDFRID = #{user.userid }, FXD_NTC_YN = #{fxdNtcYn },
FXD_NTC_STRT_DT= #{fxdNtcStrtDt}, FXD_NTC_END_DT = #{fxdNtcEndDt },
FXD_NTC_STRT_DT= null, FXD_NTC_END_DT = null,
LOGIN_YN = #{loginYn } WHERE PSTID = #{pstid}
