mapper.select:
/* BbsDao.selectPstList */
SELECT A.* FROM ( SELECT A.PSTID, A.TITLE, A.HITS, CASE WHEN FXD_NTC_YN = 'Y' THEN 1 WHEN FXD_NTC_YN = 'N' THEN 2 ELSE 3 END AS FXD_NTC_YN_ORD, FXD_NTC_YN, FXD_NTC_STRT_DT, FXD_NTC_END_DT, IFNULL(B.FILECNT,0) file_cnt, B.NM , CONVERT(A.REG_DT, CHAR(10)) REG_DT, CONVERT(A.UPDT_DT, CHAR(10)) UPDT_DT, A.UPDT_DT ord_DT, A.LOGIN_YN FROM TB_PST A LEFT OUTER JOIN (SELECT FILEGRPID, COUNT(FILEID) FILECNT FROM TB_FILE GROUP BY FILEGRPID) B ON A.FILEGRPID =B.FILEGRPID INNER JOIN TB_USER B ON(A.REGUSERID = B.USERID) WHERE 1=1 AND (CASE WHEN FXD_NTC_YN ='Y' AND FXD_NTC_STRT_DT <= NOW() AND DATE_ADD(FXD_NTC_END_DT, INTERVAL 1 DAY) >= NOW() THEN 'Y' WHEN FXD_NTC_YN ='Y' AND FXD_NTC_STRT_DT = null AND FXD_NTC_END_DT = null THEN 'Y' WHEN FXD_NTC_YN ='N' THEN 'Y' ELSE 'N' END )= 'Y'
AND BBSID= #{bbsid}
AND A.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CNTNTS LIKE CONCAT('%', #{searchKeyword}, '%')
) A WHERE 1=1
AND A.NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.LOGIN_YN = 'N'
