mapper.select:
/* BookDao.selectBookList */
SELECT A.BOOKID , A.TTL , CONCAT(B2.CD_NM, ' > ', B1.CD_NM) AS EDUSBJCTCD , C1.CD_NM AS EDUTRGTCD , A.WRITR , A.WRITR_PICTR , A.PLSCMPN , A.RPRS_IMG_FILEID , E.FILE_IDNTFC_KEY , NVL(A.HITS,0) AS HITS , DATE(A.REG_DT) AS REG_DT FROM TB_CMM_BOOK A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID WHERE 1=1
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%') AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND B2.CD = #{searchManinEduSbjctCd}
AND B1.CD = #{searchMdleEduSbjctCd}
AND C1.CD = #{searchEduTrgtCd}
AND A.PLSCMPN LIKE CONCAT('%',#{searchPlscmpn},'%')
AND DATE(A.REG_DT) >= #{startDt}
AND DATE(A.REG_DT) <= #{endDt}

/* BookDao.selectBookInfo */ SELECT A.BOOKID , B1.CD_NM AS EDU_SBJCT_CD , C1.CD_NM AS EDU_TRGT_CD , A.WRITR , A.WRITR_PICTR , A.PLSCMPN , FORMAT(A.AMT, 0) AS AMT , NVL(A.HITS,0) AS HITS , A.TTL , A.RPRS_IMG_FILEID , E.FILE_IDNTFC_KEY , A.CN , DATE(A.REG_DT) AS REG_DT
, (SELECT BOOKID FROM tb_cmm_book WHERE BOOKID > #{bookid} LIMIT 1) AS NEXT_BOOKID , (SELECT TTL FROM tb_cmm_book WHERE BOOKID > #{bookid} LIMIT 1) AS NEXT_BOOK_TTL , (SELECT BOOKID FROM tb_cmm_book WHERE BOOKID < #{bookid} ORDER BY BOOKID DESC LIMIT 1) AS BEFORE_BOOKID , (SELECT TTL FROM tb_cmm_book WHERE BOOKID < #{bookid} ORDER BY BOOKID DESC LIMIT 1) AS BEFORE_BOOK_TTL
FROM TB_CMM_BOOK A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID LEFT OUTER JOIN TB_CMM_USER F ON A.RGTRID = F.USERID WHERE BOOKID = #{bookid}
