mapper.select:
/* CarbonEnveduDao.selectEnveduList */
SELECT A.ENVEDUID , A.CLSF_CD , A.ENVEDU_SE_CD , D.CD_NM AS ENVEDUSECDNM , CONCAT(A.YR, '-', A.MM) AS YRMM , A.YR , A.MM , A.QU , A.TTL , A.TYPE_CD , A.URL , A.THMBN_FILEID , C.FILEGRPID , C.FILE_IDNTFC_KEY , A.CN_STYLE , A.CN , A.MDFCN_DT , A.MDFRID , A.REG_DT AS REGDT , A.RGTRID , B.NM FROM TB_CMM_WBZN_ENVEDU A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID LEFT OUTER JOIN TB_CMM_CD D ON A.ENVEDU_SE_CD = D.CD WHERE 1 = 1 AND A.CLSF_CD = '232102'
AND A.QU = #{compareQuarter} AND A.YR = #{compareYear}
AND A.QU = #{compareQuarter} AND A.YR = #{compareYear}
AND CASE WHEN DATE_FORMAT(NOW(), '%m') >= '01' AND DATE_FORMAT(NOW(), '%m') <= '03' THEN A.QU = 1 WHEN DATE_FORMAT(NOW(), '%m') >= '04' AND DATE_FORMAT(NOW(), '%m') <= '06' THEN A.QU = 2 WHEN DATE_FORMAT(NOW(), '%m') >= '07' AND DATE_FORMAT(NOW(), '%m') <= '09' THEN A.QU = 3 WHEN DATE_FORMAT(NOW(), '%m') >= '10' AND DATE_FORMAT(NOW(), '%m') <= '12' THEN A.QU = 4 END AND A.YR = DATE_FORMAT(NOW(), '%Y')
AND A.YR = #{enveduSearchYr}
AND A.QU = #{enveduSearchMm}

/* PrgrmgdDao.selectPrgrmgdList */
SELECT A.PRGRMID , A.CLSF_CD , A.RGN_CD , D.CD_NM AS RGNCDNM , CONCAT(A.YR, '-' ,A.MM) AS YRMM , A.YR , A.MM , A.TTL , A.SCHDL , A.URL , A.THMBN_FILEID , C.FILEGRPID , C.FILE_IDNTFC_KEY , A.CN_STYLE , A.CN , A.CN_SUMRY , A.MDFCN_DT , A.MDFRID , A.REG_DT AS REGDT , A.RGTRID , B.NM FROM TB_CMM_WBZN_PRGRM A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID LEFT OUTER JOIN TB_CMM_CD D ON A.RGN_CD = D.CD WHERE 1=1 AND A.CLSF_CD = '232102'
AND A.QU = #{compareQuarter} AND A.YR = #{compareYear}
AND A.QU = #{compareQuarter} AND A.YR = #{compareYear}
AND CASE WHEN DATE_FORMAT(NOW(), '%m') >= '01' AND DATE_FORMAT(NOW(), '%m') <= '03' THEN A.QU = 1 WHEN DATE_FORMAT(NOW(), '%m') >= '04' AND DATE_FORMAT(NOW(), '%m') <= '06' THEN A.QU = 2 WHEN DATE_FORMAT(NOW(), '%m') >= '07' AND DATE_FORMAT(NOW(), '%m') <= '09' THEN A.QU = 3 WHEN DATE_FORMAT(NOW(), '%m') >= '10' AND DATE_FORMAT(NOW(), '%m') <= '12' THEN A.QU = 4 END AND A.YR = DATE_FORMAT(NOW(), '%Y')
AND A.YR = #{prgrmgdSearchYr}
AND A.QU = #{prgrmgdSearchMm}
