mapper.select:
/* CntntsDao.selectCntntsList */
SELECT A.CNTNTSID , A.TTL , CONCAT(B2.CD_NM, ' > ', B1.CD_NM) AS EDUSBJCTCD , C1.CD_NM AS EDUTRGTCD , CONCAT(D2.CD_NM, ' > ', D1.CD_NM) AS TYPECD , D1.CD_NM AS MAINTYPECD , NVL(A.HITS,0) AS HITS , A.RPRS_IMG_FILEID , E.FILE_IDNTFC_KEY , DATE(A.REG_DT) AS REG_DT FROM TB_CMM_CNTNTS A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD LEFT OUTER JOIN TB_CMM_CD D1 ON A.TYPE_CD = D1.CD LEFT OUTER JOIN TB_CMM_CD D2 ON D1.UPPR_CD = D2.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID WHERE 1=1
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%') AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND B2.CD = #{searchManinEduSbjctCd}
AND B1.CD = #{searchMdleEduSbjctCd}
AND C1.CD = #{searchEduTrgtCd}
AND D2.CD = #{searchMainTypeCd}
AND D1.CD = #{searchMdleTypeCd}
AND DATE(A.REG_DT) >= #{startDt}
AND DATE(A.REG_DT) <= #{endDt}

/* CntntsDao.selectCntntsInfo */ SELECT A.CNTNTSID , B1.CD_NM AS EDU_SBJCT_CD , B2.CD_NM AS MAIN_EDU_SBJCT_CD , C1.CD_NM AS EDU_TRGT_CD , D1.CD_NM AS TYPE_CD , D2.CD_NM AS MAIN_TYPE_CD , A.ORIGIN , NVL(A.HITS,0) AS HITS , A.MNFCT_YY , A.PLY_SECND , A.TTL , A.CN , DATE(A.REG_DT) AS REG_DT , A.RGTRID , CONCAT(F.ACNT,'(',F.NM,')') AS rgtridNm , A.REG_DT
, (SELECT CNTNTSID FROM tb_cmm_cntnts WHERE CNTNTSID > #{cntntsid} LIMIT 1) AS NEXT_CNTNTSID , (SELECT TTL FROM tb_cmm_cntnts WHERE CNTNTSID > #{cntntsid} LIMIT 1) AS NEXT_CNTNTS_TTL , (SELECT CNTNTSID FROM tb_cmm_cntnts WHERE CNTNTSID < #{cntntsid} ORDER BY CNTNTSID DESC LIMIT 1) AS BEFORE_CNTNTSID , (SELECT TTL FROM tb_cmm_cntnts WHERE CNTNTSID < #{cntntsid} ORDER BY CNTNTSID DESC LIMIT 1) AS BEFORE_CNTNTS_TTL
FROM TB_CMM_CNTNTS A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD LEFT OUTER JOIN TB_CMM_CD D1 ON A.TYPE_CD = D1.CD LEFT OUTER JOIN TB_CMM_CD D2 ON D1.UPPR_CD = D2.CD
LEFT OUTER JOIN TB_CMM_USER F ON A.RGTRID = F.USERID WHERE CNTNTSID = #{cntntsid}
