mapper.update:
UPDATE TB_CMM_CNTST_APLY SET
    APLY_SE_CD        = #{aplySeCd}
    , PCNTST_FLD_CD   = #{pcntstFldCd}
    , APLY_PATH_CD    = #{aplyPathCd}
    , BRDT            = #{brdt}
    , PRDCT_TTL       = #{prdctTtl}
    , PRDCT_EXPLN     = #{prdctExpln}
    , PRDCT_FILEGRPID = #{prdctFilegrpid}
 WHERE
    APLYID = #{aplyid}

update.foreach:
UPDATE TB_CMM_CNTST_APLY_SCHL SET
	SCHL_NM       = #{item.schlNm}
  , TCHER_NM      = #{item.tcherNm}
  , TCHER_GNDR    = #{item.tcherGndr}
  , TELNO         = #{item.telno}
  , PCNTST_FLD_CD = #{item.pcntstFldCd}
  , STDNT_MALE    = #{item.stdntMale}
  , STDNT_FEMALE  = #{item.stdntFemale}
WHERE
	APLY_SCHLID = #{item.aplySchlid}
