mapper.insert:
INSERT INTO TB_CMM_ENV_EDU_PRPSL(
    CLSF_CD
    , TTL
    , CN
    , USERID
    , INSTID
    , RLS_YN
    , DEL_YN
    , PRCS_STTS_CD
    , FILEGRPID
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
) VALUES (
    #{clsfCd}
    , #{ttl}
    , #{cn}
    , #{user.userid}
    , #{user.instid}
    , #{rlsYn}
    , 'N'
    , '113101'
    , #{filegrpid}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.update:
UPDATE TB_CMM_ENV_EDU_PRPSL SET
    DEL_YN = 'Y'
    , MDFRID = #{user.userid}
    , MDFCN_DT = NOW()
WHERE PRPSLID = #{prpslid}

UPDATE TB_CMM_ENV_EDU_PRPSL SET
    PRCS_STTS_CD = '113102'
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE PRPSLID = #{prpslid}

UPDATE TB_CMM_ENV_EDU_PRPSL SET
    CLSF_CD = #{clsfCd}
    , TTL = #{ttl}
    , CN = #{cn}
    , RLS_YN = #{rlsYn}
    , FILEGRPID = #{filegrpid}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE PRPSLID = #{prpslid}

mapper.select:
SELECT A.PRPSLID , A.CLSF_CD , A.TTL , A.CN , A.RLS_YN , A.PRCS_STTS_CD , A.REG_DT , A.USERID , B.NM FROM TB_CMM_ENV_EDU_PRPSL A INNER JOIN TB_CMM_USER B ON A.USERID = B.USERID WHERE A.DEL_YN != 'Y' AND A.PRCS_STTS_CD != '113102'
AND A.TTL LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CLSF_CD = #{clsfCd}
