mapper.select:
/* EnvReqstDao.selectEnvReqstList*/
SELECT A.SPCEID , B.FCLTID , B.FCLT_NM , B.ADDR , B.ADDR_DTL , B.RPRS_IMG_FILEID , B.APLY_MTHD_CD , C.INSTID , C.INST_NM , D.FILE_IDNTFC_KEY , F.BGNG_DT , F.END_DT FROM TB_FCL_SPCE A INNER JOIN TB_FCL_FCLT B ON A.FCLTID = B.FCLTID LEFT OUTER JOIN TB_CMM_INST C ON B.INSTID = C.INSTID LEFT OUTER JOIN TB_CMM_FILE D ON B.RPRS_IMG_FILEID = D.FILEID INNER JOIN ( SELECT SPCEID , MIN(BGNG_DT) BGNG_DT , MAX(END_DT) END_DT FROM TB_FCL_SPCE_RSVTDE F GROUP BY SPCEID ) F ON A.SPCEID = F.SPCEID WHERE 1 = 1 AND A.USE_YN = 'Y' AND B.USE_YN = 'Y'
AND C.INST_NM LIKE CONCAT('%',#{instNm},'%')
AND B.FCLT_NM LIKE CONCAT('%',#{fcltNm},'%')
AND A.MAX_NOPE = #{maxNope}
AND SUBSTRING( B.RGN_CD,1,2) = #{searchSiGunGuCd}
AND B.APLY_MTHD_CD = #{searchAplyMthdCd}
AND B.BGNG_DT >= CONCAT(#{searchBgngDt},' 00:00:00')
AND B.END_DT <= CONCAT(#{searchEndDt},' 23:59:59')

/* EnvReqstDao.selectSpceInfo */ SELECT A.SPCEID , A.FCLTID , A.SPCE_NM , A.SPCE_NO , A.MAX_NOPE , A.SIZE , A.CHKIN_HR , A.CHCKT_HR , A.NXTY_CHCKT_YN , A.RSVT_PSBLTY_DAYCNT , A.TODAY_RSVT_PSBLTY_YN , A.USE_YN , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID , B.BANK_CD , B.BACNT_NO , B.DPSTR_NM , B.FCLTID , B.FCLT_NM , B.FCLT_NO , B.ADDR , B.ADDR_DTL , B.APLY_MTHD_CD , C.INST_NM , C.INSTID , F.BGNG_DT , F.END_DT FROM TB_FCL_SPCE A INNER JOIN TB_FCL_FCLT B ON A.FCLTID = B.FCLTID INNER JOIN TB_CMM_INST C ON B.INSTID = C.INSTID INNER JOIN (SELECT SPCEID, MIN(BGNG_DT) BGNG_DT, MAX(END_DT) END_DT FROM TB_FCL_SPCE_RSVTDE F WHERE RSVTDEID IN(
#{item.rsvtdeid}
) GROUP BY SPCEID) F ON A.SPCEID = F.SPCEID WHERE 1=1 AND A.SPCEID = #{spceid}

mapper.insert:
/* EnvReqstDao.insertResveEnvFclAply */
INSERT INTO TB_FCL_APLY_RSVTDE (
        APLYID,
        RSVTDEID,
        UTZTN_SE_CD,
        ALLDAY_YN,
        DE,
        BGNG_DT,
        END_DT,
        AMT,
        MDFCN_DT,
        MDFRID,
        REG_DT,
        RGTRID
)VALUES

     (
    #{aplyid}
    ,#{item}
    ,#{utztnSeCd}
    ,#{alldayYn}
    ,#{startDt}
    ,#{startDt}
    ,#{enddDt}
    ,#{amt}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
    )

mapper.select:
/* SpceDao.selectFclRsvtdeList */ SELECT RSVTDEID, SPCEID, DE, AMT, ALLDAY_YN, RSVT_PSBLTY_YN FROM TB_FCL_SPCE_RSVTDE A WHERE 1 = 1 AND A.RSVTDEID IN (
#{item.rsvtdeid}
)

/* SpceDao.selectSpceRsvtdeList */ SELECT A.RSVTDEID ,DE ,date_format(A.BGNG_DT, '%H:%i' ) AS STRT_TM ,date_format(A.END_DT, '%H:%i' ) AS END_TM ,A.UTZTN_SE_CD ,A.ALLDAY_YN ,A.AMT ,A.RSVT_PSBLTY_YN ,A.BGNG_DT ,A.END_DT ,IFNULL(B.RSVTDEID, '0') AS FULL_YN ,C.TODAY_RSVT_PSBLTY_YN FROM TB_FCL_SPCE_RSVTDE A LEFT OUTER JOIN ( SELECT B.RSVTDEID FROM TB_FCL_APLY_RSVTDE B INNER JOIN TB_FCL_SPCE_APLY C ON B.APLYID = C.APLYID WHERE 1=1 AND C.APLY_STTS_CD IN ('160101','160102') GROUP BY B.RSVTDEID ) B ON A.RSVTDEID = B.RSVTDEID INNER JOIN TB_FCL_SPCE C ON A.SPCEID = C.SPCEID WHERE 1=1 AND A.RSVT_PSBLTY_YN = 'Y' AND A.SPCEID = #{spceid}
AND A.UTZTN_SE_CD = '169102'
AND A.UTZTN_SE_CD = '169101'
AND A.DE >= #{startDt}
AND A.DE <= #{enddDt}
AND A.DE IN (
#{item.de}
)
AND A.DE = #{de}
