mapper.select:
/* EnveduDao.selectEnveduList */
SELECT A.ENVEDUID , A.CLSF_CD , A.ENVEDU_SE_CD , D.CD_NM AS ENVEDUSECDNM , CONCAT(A.YR, '-' ,A.MM) AS YRMM , A.YR , A.MM , A.QU , A.TTL , A.TYPE_CD , A.URL , A.THMBN_FILEID , C.FILEGRPID , C.FILE_IDNTFC_KEY , A.CN_STYLE , A.CN , A.MDFCN_DT , A.MDFRID , A.REG_DT AS REGDT , A.RGTRID , B.NM FROM TB_CMM_WBZN_ENVEDU A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID LEFT OUTER JOIN TB_CMM_CD D ON A.ENVEDU_SE_CD = D.CD WHERE 1=1 AND A.CLSF_CD = '232101'
AND A.MM = DATE_FORMAT(#{compareDate}, '%m') AND A.YR = DATE_FORMAT(#{compareDate}, '%Y')
AND A.MM = DATE_FORMAT(#{compareDate}, '%m') AND A.YR = DATE_FORMAT(#{compareDate}, '%Y')
AND A.MM = DATE_FORMAT(NOW(), '%m') AND A.YR = DATE_FORMAT(NOW(), '%Y')
AND A.YR = #{enveduSearchYr}
AND A.MM = #{enveduSearchMm}

/* PrgrmgdDao.selectPrgrmgdList */
SELECT A.PRGRMID , A.CLSF_CD , A.RGN_CD , D.CD_NM AS RGNCDNM , CONCAT(A.YR, '-' ,A.MM) AS YRMM , A.YR , A.MM , A.TTL , A.SCHDL , A.URL , A.THMBN_FILEID , C.FILEGRPID , C.FILE_IDNTFC_KEY , A.CN_STYLE , A.CN , A.CN_SUMRY , A.MDFCN_DT , A.MDFRID , A.REG_DT AS REGDT , A.RGTRID , B.NM FROM TB_CMM_WBZN_PRGRM A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID LEFT OUTER JOIN TB_CMM_CD D ON A.RGN_CD = D.CD WHERE 1=1 AND A.CLSF_CD = '232101'
AND A.MM = DATE_FORMAT(#{compareDate}, '%m') AND A.YR = DATE_FORMAT(#{compareDate}, '%Y')
AND A.MM = DATE_FORMAT(#{compareDate}, '%m') AND A.YR = DATE_FORMAT(#{compareDate}, '%Y')
AND A.MM = DATE_FORMAT(NOW(), '%m') AND A.YR = DATE_FORMAT(NOW(), '%Y')
AND A.YR = #{prgrmgdSearchYr}
AND A.MM = #{prgrmgdSearchMm}
