mapper.select:
/* InfntAplyDao.selectInfntAplyList */
SELECT A.PRGRMID , A.CLSSRMID , B.CLSSRM_NM , A.PRGRM_NM , F.CD_NM AS CLSF_NM , A.EDU_HR_MNT , G.FILEID , G.FILE_IDNTFC_KEY , E.CTPRVN_NM ,(SELECT GROUP_CONCAT(TB.CD_NM SEPARATOR', ') FROM TB_EDU_INFNT_PRGRM_TRGT_MAPNG TA, TB_CMM_CD TB WHERE TA.PRGRMID = A.PRGRMID and TA.TRGT_CD = TB.CD) AS TRGT_NM , CASE WHEN NOW() <= DATE_FORMAT(( SELECT MAX(T4.DE) DE FROM TB_EDU_CLSSRM T1 LEFT OUTER JOIN TB_EDU_INFNT_PRGRM T2 ON T2.CLSSRMID = T1.CLSSRMID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TME T3 ON T3.PRGRMID = T2.PRGRMID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TME_SCHDL T4 ON T4.TMEID = T3.TMEID WHERE 1=1 AND T1.CLSSRMID = B.CLSSRMID AND T2.PRGRMID <> A.PRGRMID GROUP BY T1.CLSSRMID),'%Y.%m.%d') THEN '모집전' ELSE CASE WHEN B.RCPT_BGNG_DT > NOW() THEN '모집전' WHEN NOW() BETWEEN B.RCPT_BGNG_DT AND B.RCPT_END_DT THEN '모집중' WHEN B.RCPT_END_DT < NOW() THEN '마감' ELSE '마감' END END AS RCPT_DESC , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID FROM TB_EDU_INFNT_PRGRM A LEFT OUTER JOIN TB_EDU_CLSSRM B ON A.CLSSRMID = B.CLSSRMID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_CLSF_MAPNG C ON A.PRGRMID = C.PRGRMID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TRGT_MAPNG D ON A.PRGRMID = D.PRGRMID LEFT OUTER JOIN TB_CMM_ADDR_CTPRVN E ON E.CTPRVN_CD = B.CTPRVN_CD LEFT OUTER JOIN TB_CMM_CD F ON F.CD = C.CLSF_CD LEFT OUTER JOIN TB_CMM_FILE G ON A.EDU_PHOTO_FILEID = G.FILEID WHERE 1=1 AND A.USE_YN = 'Y'
AND A.PRGRM_NM LIKE CONCAT('%',#{searchPrgrmNm},'%')
AND B.CLSSRM_NM LIKE CONCAT('%',#{searchEduClssRmNm},'%')
AND D.TRGT_CD = #{searchTrgtCd}
AND B.CTPRVN_CD = #{searchCtprvnCd}
AND F.UPPR_CD = #{searchClsfCd1}
AND C.CLSF_CD = #{searchClsfCd2}
GROUP BY A.PRGRMID, C.CLSF_CD

mapper.insert:
/* InfntAplyDao.insertInfntAply */
INSERT INTO TB_EDU_INFNT_PRGRM_APLY (
    INSTID
    , USERID
    , TME_SCHDLID
    , ADDR
    , ADDR_DTL
    , CTPRVN_CD
    , TELNO
    , MOBLPHON
    , EML
    , EDU_NOPE
    , TCHER_NOPE
    , ETC
    , TOWWAY_ONLN_EDU_AGRE_YN
    , STTS_CD
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)
SELECT 
     NULLIF(#{instid},'')
    ,#{aplyUserid}
    ,#{tmeSchdlid}
    ,#{addr}
    ,#{addrDtl}
    ,#{ctprvnCd}
    ,#{telno}
    ,#{moblphon}
    ,#{eml}
    ,#{eduNope}
    ,#{tcherNope}
    ,#{etc}
    ,#{towwayOnlnEduAgreYn}
    ,CASE WHEN C.RCPT_MTHD_CD = '156101' THEN '180102' ELSE CASE WHEN COUNT(D.STTS_CD) <= C.EDU_NOPE THEN '180102' ELSE '180101' END END    
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
  FROM TB_EDU_INFNT_PRGRM_TME_SCHDL A
  LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TME B ON B.TMEID = A.TMEID
  LEFT OUTER JOIN TB_EDU_INFNT_PRGRM C ON C.PRGRMID = B.PRGRMID                
  LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_APLY D ON D.TME_SCHDLID = #{tmeSchdlid}                 
 WHERE A.TME_SCHDLID = #{tmeSchdlid}

mapper.select:
/* InfntAplyDao.selectInfntAplyHistList */
SELECT A.APLYID , D.PRGRMID , E.CLSSRMID , E.CLSSRM_NM , D.PRGRM_NM , F.CD_NM CTPRVN_NM , B.DE , C.TMEID , C.TME_NM , A.STTS_CD , G.CD_NM STTS_NM , DATE_FORMAT(A.REG_DT, '%Y-%m,%d') REG_DT , A.RGTRID FROM TB_EDU_INFNT_PRGRM_APLY A LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TME_SCHDL B ON B.TME_SCHDLID = A.TME_SCHDLID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM_TME C ON C.TMEID = B.TMEID LEFT OUTER JOIN TB_EDU_INFNT_PRGRM D ON D.PRGRMID = C.PRGRMID LEFT OUTER JOIN TB_EDU_CLSSRM E ON E.CLSSRMID = D.CLSSRMID LEFT OUTER JOIN TB_CMM_CD F ON F.CD = E.CTPRVN_CD LEFT OUTER JOIN TB_CMM_CD G ON G.CD = A.STTS_CD WHERE 1=1 AND A.USERID = #{user.userid}
AND D.PRGRM_NM LIKE CONCAT('%', #{searchKeyword}, '%')
AND E.CLSSRM_NM LIKE CONCAT('%', #{searchKeyword}, '%')
