mapper.select:
/* LendDao.selectRequestLendRndList */ SELECT A.RNDID ,A.ORDR -- 차수 ,A.QNTY -- 수량 ,A.BGNG_DE ,A.END_DE FROM TB_THA_PACKAGE_LEND_RND A WHERE 1=1 AND RNDID IN(
#{item.rndid}
) ORDER BY A.ORDR ASC

/* LendDao.checkOverStockYn */ SELECT MAX(CASE WHEN IFNULL(A.QNTY,0) - IFNULL(B.RESERVATION_CNT,0) -C.Qnty < 0 THEN 'Y' ELSE 'N' END) isOverCnt FROM TB_THA_PACKAGE_LEND_RND A LEFT OUTER JOIN( SELECT A.RNDID -- 차시 아이디 ,IFNULL(SUM(A.QNTY),0) AS RESERVATION_CNT FROM TB_THA_PACKAGE_LEND_APLY_TRGT A INNER JOIN TB_THA_PACKAGE_LEND_APLY B ON A.APLYID = B.APLYID WHERE 1=1 AND B.STTS_CD = '231102' GROUP BY A.RNDID ) B ON A.RNDID = B.RNDID INNER JOIN (
SELECT #{item.rndid} AS RNDID ,#{item.qnty} AS Qnty
) C ON A.RNDID = C.RNDID WHERE 1=1 AND A.RCRITID = #{rcritid}
