mapper.select:
/* LendDao.selectLendHistList */
SELECT C.RCRIT_NM ,C.RCRITID ,A.APLYID ,A.PACKAGEID ,B.RNDID ,B.ORDR ,A.QNTY ,A.LEND_BGNG_DE ,A.LEND_END_DE ,A.STTS_CD ,A.DLVY_STTS_CD ,C.RPRS_IMG_FILEID ,MAX( CASE WHEN D.DLIVY_PRCS_DT IS NOT NULL AND D.WRHOUSNG_PRCS_DT IS NULL AND A.LEND_END_DE < DATE_FORMAT(NOW(),'%Y-%m-%d') THEN 'Y' ELSE 'N' END ) IS_LATE_YN ,MAX( CASE WHEN D.DLIVY_PRCS_DT IS NOT NULL AND D.WRHOUSNG_PRCS_DT IS NOT NULL AND D.DLIVY_PICID IS NOT NULL THEN 'Y' ELSE 'N' END ) IS_COMPLETE_YN ,A.REG_DT ,E.FILE_IDNTFC_KEY ,A.RVW_SCR ,A.RVW_CN ,A.RVW_REG_DT FROM TB_THA_PACKAGE_LEND_APLY A INNER JOIN TB_THA_PACKAGE_LEND_RND B ON A.RNDID = B.RNDID INNER JOIN TB_THA_PACKAGE_LEND_RCRIT C ON B.RCRITID = C.RCRITID LEFT OUTER JOIN TB_THA_PACKAGE_LEND_APLY_DLIVY D ON (A.APLYID = D.APLYID AND A.RNDID = D.RNDID) LEFT OUTER JOIN TB_CMM_FILE E ON C.RPRS_IMG_FILEID = E.FILEID WHERE 1=1 AND A.RGTRID = #{user.userid}
AND RCRIT_NM LIKE CONCAT('%',#{rcritNm},'%')
GROUP BY A.APLYID

mapper.update:
  /* LendDao.updateLendAply */
  UPDATE TB_THA_PACKAGE_LEND_APLY
    SET 
        RVW_SCR       = null,  
        RVW_CN        = null,  
        RVW_FILEGRPID = null,  
        RVW_REG_DT    = null,  
        MDFCN_DT      = NOW(),  
        MDFRID        = #{user.userid}     
WHERE APLYID = #{aplyid}
