mapper.insert:
        /* MemberDao.insertMember */
        INSERT INTO TB_USER (
			ACNT
			,PWD
			,NM
			,MOBNO
			,EMAIL
			,TOS_AGRE_YN
			,STTS_CD
			,USER_SE_CD
			,UPDT_DT
			,UPDTUSERID
			,REG_DT
			,REGUSERID
        ) VALUES (
        	 #{acnt         }
			,#{pwd          }
			,#{nm           }
			,#{mob_no       }
			,#{email        }
			,#{tos_agre_yn  }
			,#{stts_cd      }
			,#{user_se_cd   }
			,NOW()
			,#{user.userid  }
			,NOW()
			,#{user.userid  }
        )

mapper.update:
      /* MemberDao.updateMember */
      UPDATE TB_USER 
       SET
       	ACNT          = #{acnt         }  
		,PWD          = #{pwd          } 
		,NM           = #{nm           } 
		,MOBNO        = #{mob_no       } 
		,EMAIL        = #{email        } 
		,USER_SE_CD   = #{user_se_cd} 
		,UPDT_DT      = NOW()
		,UPDTUSERID   = #{userid } 
	  WHERE  USERID = #{userid  }

mapper.insert:
