		  ,REGUSERID
        ) VALUES (
         #{userid}        
         ,#{schl_nm    }  
         ,#{schl_cd    }  
         ,#{chrg_crs_nm}  
		 ,#{chrg_crs_cd}  
		 ,#{cls_tchr_yn}  
		 ,NOW()  
		 ,#{userid }  
		 ,NOW()  
		 ,#{userid  }    
        )

mapper.update:
      /* MemberDao.updateMemberTeacher */
      UPDATE TB_USER_TEACHER 
        SET
		   SCHL_NM      =  #{schl_nm     }
		  ,SCHL_CD      =  #{schl_cd     }
		  ,CHRG_CRS_NM  =  #{chrg_crs_nm }
		  ,CHRG_CRS_CD  =  #{chrg_crs_cd }
		  ,CLS_TCHR_YN  =  #{cls_tchr_yn }
		  ,UPDT_DT      =  NOW()
		  ,UPDTUSERID   =  #{userid  }
	  WHERE USERID = #{userid  }

mapper.insert:
        /* MemberDao.insertMember */
        INSERT INTO TB_ROLE_USER (
		   ROLEID
           ,USERID
