mapper.insert:
/* MsgDao.insertMsg */
INSERT INTO TB_CMM_MSG
(
    MSGID,
    USERID,
    TRGTID,
    CN,
    INQ_YN,
    USER_DEL_YN,
    TRGT_DEL_YN,
    MDFCN_DT,
    MDFRID,
    REG_DT,
    RGTRID
) VALUES (
     #{msgid}
     , #{user.userid}
     , #{trgtid}
     , #{cn}
     , 'N'
     , 'N'
     , 'N'
     , NOW()
     , #{user.userid}
     , NOW()
     , #{user.userid}
)

mapper.select:
/* MsgDao.selectTrgtInfo */
SELECT
    ACNT TRGT_ACNT
    , NM TRGT_NM
FROM TB_CMM_USER
WHERE USERID = #{trgtId}
