mapper.select:
/* MvmnAplyHistDao.selectMvmnAplyHistInfo */ SELECT A.APLYID , D.PRGRMID , J.SAREAID , E.SAREA_NM , D.PRGRM_NM , D.EDU_NOPE , D.EDU_HR_MNT , D.OPER_FOM_CD , (SELECT GROUP_CONCAT(TC.TRGT_CD SEPARATOR',') FROM TB_EDU_MVMN_PRGRM_TRGT_MAPNG TC WHERE TC.PRGRMID = D.PRGRMID) AS TRGT_CD , (SELECT GROUP_CONCAT(TB.CD_NM SEPARATOR',') FROM TB_EDU_MVMN_PRGRM_TRGT_MAPNG TA, TB_CMM_CD TB WHERE TA.PRGRMID = D.PRGRMID and TA.TRGT_CD = TB.CD) AS TRGT_NM , GROUP_CONCAT(REPLACE(B.DE, '.', '-')SEPARATOR', ') DE , C.TMEID , C.TME_NM , A.STTS_CD , G.CD_NM STTS_NM , D.APLCNT_DGSTFN_SRVYID , D.STDNT_DGSTFN_SRVYID , H.QESTNRID APLCNT_DGSTFN_QESTNRID , I.QESTNRID STDNT_DGSTFN_QESTNRID , J.SBMSNID , CASE WHEN J.SBMSN_DT IS NULL THEN 'N' ELSE 'Y' END SBMSN_YN , DATE_FORMAT(A.REG_DT, '%Y-%m-%d') REG_DT , A.RGTRID FROM TB_EDU_MVMN_PRGRM_APLY A LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_APLY_SCHDL Z ON Z.APLYID = A.APLYID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME_SCHDL B ON B.TME_SCHDLID = Z.TME_SCHDLID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME C ON C.TMEID = B.TMEID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM D ON D.PRGRMID = C.PRGRMID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_SCHDL J ON J.PRGRM_SCHDLID = B.PRGRM_SCHDLID LEFT OUTER JOIN TB_EDU_SAREA E ON E.SAREAID = J.SAREAID LEFT OUTER JOIN TB_CMM_CD G ON G.CD = A.STTS_CD LEFT OUTER JOIN TB_CMM_SRVY H ON H.SRVYID = D.APLCNT_DGSTFN_SRVYID LEFT OUTER JOIN TB_CMM_SRVY I ON I.SRVYID = D.STDNT_DGSTFN_SRVYID LEFT OUTER JOIN (SELECT SRVYID, SBMSNID, MVMN_PRGRM_APLYID, MAX(SBMSN_DT) SBMSN_DT FROM TB_CMM_SRVY_SBMSN GROUP BY SRVYID, MVMN_PRGRM_APLYID) J ON J.SRVYID = D.APLCNT_DGSTFN_SRVYID AND J.MVMN_PRGRM_APLYID = A.APLYID WHERE 1=1 AND A.APLYID = #{aplyid}
AND B.TME_SCHDLID = #{tmeSchdlid}
AND B.TMEID = #{tmeid}
GROUP BY A.APLYID

/* MvmnAplyHistDao.selectMvmnAplyHistDetail */ SELECT A.APLYID , Z.TME_SCHDLID , GROUP_CONCAT(REPLACE(B.DE, '.', '-')SEPARATOR', ') DE , C.TMEID , C.TME_NM , A.REG_DT AS APLY_DT , A.INSTID , NULLIF(E.INST_NM, '') INST_NM , NULLIF(A.USERID, '') APLY_USERID , NULLIF(F.NM, '') APLY_USER_NM , NULLIF(A.ADDR, '') ADDR , NULLIF(A.ADDR_DTL, '') ADDR_DTL , NULLIF(A.TELNO, '') TELNO , NULLIF(F.MOBLPHON, '') MOBLPHON , NULLIF(A.EML, '') EML , NULLIF(A.FXNO, '') FXNO , NULLIF(A.HOPE_EDU_BGNG_TM, '') HOPE_EDU_BGNG_TM , NULLIF(A.HOPE_EDU_END_TM, '') HOPE_EDU_END_TM , A.EDU_NOPE , NULLIF(A.RMRK, '') RMRK , A.STTS_CD , D.CD_NM AS STTS_NM ,(SELECT GROUP_CONCAT(EDU_TRGT_CD SEPARATOR',') FROM TB_EDU_MVMN_PRGRM_APLY_EDU_TRGT WHERE APLYID = #{aplyid} ) AS TRGT_CD ,(SELECT GROUP_CONCAT(TB.CD_NM SEPARATOR', ') FROM TB_EDU_MVMN_PRGRM_APLY_EDU_TRGT TA, TB_CMM_CD TB WHERE TA.APLYID = #{aplyid} and TA.EDU_TRGT_CD = TB.CD) AS TRGT_NM , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID FROM TB_EDU_MVMN_PRGRM_APLY A LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_APLY_SCHDL Z ON Z.APLYID = A.APLYID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME_SCHDL B ON B.TME_SCHDLID = Z.TME_SCHDLID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME C ON C.TMEID = B.TMEID LEFT OUTER JOIN TB_CMM_CD D ON D.CD = A.STTS_CD LEFT OUTER JOIN TB_CMM_INST E ON E.INSTID = A.INSTID LEFT OUTER JOIN TB_CMM_USER F ON F.USERID = A.USERID WHERE A.APLYID = #{aplyid}
AND B.TME_SCHDLID = #{tmeSchdlid}
AND B.TMEID = #{tmeid}
GROUP BY A.APLYID

mapper.update:
/* MvmnAplyHistDao.updateCancelMvmnAply */
UPDATE TB_EDU_MVMN_PRGRM_APLY SET
    STTS_CD = #{updCd}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE APLYID = #{aplyid}

/* MvmnAplyHistDao.updateMvmnAply */
UPDATE TB_EDU_MVMN_PRGRM_APLY SET
        EDU_NOPE                  = #{eduNope}
        , HOPE_EDU_BGNG_TM        = #{hopeEduBgngTm}
        , HOPE_EDU_END_TM         = #{hopeEduEndTm}
        , RMRK                    = #{rmrk}
        , MDFCN_DT                = NOW()
        , MDFRID                  = #{user.userid}
 WHERE APLYID                     = #{aplyid}

mapper.insert:
/* MvmnAplyHistDao.deleteTrgtCd */
DELETE FROM TB_EDU_MVMN_PRGRM_APLY_EDU_TRGT
WHERE 1=1
  AND APLYID = #{aplyid}
