mapper.select:
SELECT A.APLYID , A.STLM_STTS_CD , A.APLY_STTS_CD , A.APLCNTID , A.APLCNT_NM , A.AMT , A.DPST_AMT , A.RFND_DMND_AMT , A.RFND_AMT , A.MDFCN_DT , A.RVW_CN , A.RVW_SCR , A.RVW_DT , CASE WHEN A.STLM_STTS_CD = 161101 AND A.APLY_STTS_CD = 160101 THEN '' /* 입금대기 - 예약신청 */ WHEN A.STLM_STTS_CD = 161101 AND A.APLY_STTS_CD = 160103 THEN A.RSVT_CNCL_RSN /* 입금대기 - 예약취소 - 예약취소사유 */ WHEN A.STLM_STTS_CD = 161102 AND A.APLY_STTS_CD = 160102 THEN '' /* 입금완료 - 예약승인 */ WHEN A.STLM_STTS_CD = 161103 AND A.APLY_STTS_CD = 160103 THEN '' /* 환불요청 - 예약취소 */ WHEN A.STLM_STTS_CD = 161104 AND A.APLY_STTS_CD = 160103 THEN '' /* 환불완료 - 예약취소 */ WHEN A.STLM_STTS_CD = 161102 AND A.APLY_STTS_CD = 160102 THEN A.RFND_REJECT_RSN /* 환불완료 - 예약취소 - 환불완료취소 - 환불완료취소사유 and 환불완료취소사유코드 */ ELSE '' END AS CHG_RSN , A1.CD_NM AS APLY_STTS_CD_NM , A2.CD_NM AS STLM_STTS_CD_NM , C.INSTID , C.INST_NM , B.ALLDAY_YN , B.BGNG_DT , B.END_DT , E.SPCE_NM , E.SIZE , E.MAX_NOPE , F.ADDR , F.ADDR_DTL , F.FCLT_NM , F.RGN_CD , G.ACNT , G.NM FROM TB_FCL_SPCE_APLY A INNER JOIN ( SELECT CD , CD_NM FROM TB_CMM_CD WHERE CDGRPID = 160 ) A1 ON (A.APLY_STTS_CD = A1.CD) INNER JOIN ( SELECT CD , CD_NM FROM TB_CMM_CD WHERE CDGRPID = 161 ) A2 ON (A.STLM_STTS_CD = A2.CD) INNER JOIN (SELECT B.APLYID, D.SPCEID, MIN(D.BGNG_DT) AS BGNG_DT, B.ALLDAY_YN, CASE WHEN B.ALLDAY_YN = 'N' THEN MAX(D.END_DT) ELSE MAX(D.BGNG_DT) END AS END_DT FROM TB_FCL_APLY_RSVTDE B LEFT JOIN TB_FCL_SPCE_RSVTDE D ON B.RSVTDEID = D.RSVTDEID GROUP BY APLYID) B ON A.APLYID = B.APLYID INNER JOIN TB_FCL_SPCE E ON B.SPCEID = E.SPCEID INNER JOIN TB_FCL_FCLT F ON E.FCLTID = F.FCLTID INNER JOIN TB_CMM_INST C ON F.INSTID = C.INSTID LEFT OUTER JOIN TB_CMM_USER G ON A.APLCNTID = G.USERID WHERE 1 = 1 AND A.APLCNTID = #{user.userid}
AND E.SPCE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND F.FCLT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLY_STTS_CD = #{searchAplySttsCd}
AND A.STLM_STTS_CD = #{searchStlmSttsCd}
AND B.BGNG_DT >= date(#{searchBgngDt})
AND B.END_DT <= date(#{searchEndDt})

mapper.insert:
