     (
    #{aplyid}
    ,#{item}
    ,#{utztnSeCd}
    ,#{de}
    ,#{bgngDt}
    ,#{endDt}
    ,#{amt}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
    )

mapper.select:
/* SpceDao.selectFclRsvtdeList */ SELECT RSVTDEID, SPCEID, DE, AMT, RSVT_PSBLTY_YN FROM TB_FCL_SPCE_RSVTDE A WHERE 1 = 1 AND A.RSVTDEID IN (
#{item.rsvtdeid}
)

/* MypageEnvReqstDao.selectRsnInfo */
SELECT
    RFND_REJECT_RSN
FROM TB_FCL_SPCE_APLY A
WHERE 1 = 1
AND A.APLYID = #{aplyid}

mapper.update:
/* MypageEnvReqstDao.insertRsn */
UPDATE TB_FCL_SPCE_APLY SET
    CNCL_RSN_CD                    = '215104'
  ,APLY_STTS_CD                    = '160103'
  ,RSVT_CNCL_RSN                    = #{rsvtCnclRsn}
  ,CNCL_DT                 = NOW()
  ,MDFRID                 = #{user.userid}
  ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}

/* MypageEnvReqstDao.insertRvw */
UPDATE TB_FCL_SPCE_APLY SET
    RVW_CN                    = #{rvwCn}
  ,RVW_SCR                    = #{rvwScr}
  ,RVW_DT                    = NOW()
  ,MDFRID                 = #{user.userid}
  ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}

/* MypageEnvReqstDao.deleteRvw */
UPDATE TB_FCL_SPCE_APLY SET
    RVW_CN                    = null
  ,RVW_SCR                    = 0
  ,RVW_DT                    = null
  ,MDFRID                 = #{user.userid}
  ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}
