mapper.update:
UPDATE TB_CMM_POPUPNTC
  SET
    title             =  #{title           } 
    ,cntnts           =  #{cntnts          } 
    ,expsr_lc_cd      =  #{expsrLcCd     } 
    ,menuid           =  #{menuid          } 
    ,siteid           =  #{siteid          } 
    ,login_need_yn    =  #{loginNeedYn   } 
    ,popup_type_cd    =  #{popupTypeCd   } 
    ,strt_dt          =  #{strtDt         } 
    ,end_dt           =  #{endDt          } 
    ,width_size       =  #{widthSize      } 
    ,vrtcl_size       =  #{vrtclSize      } 
    ,top_lc           =  #{topLc          } 
    ,left_lc          =  #{leftLc         } 
    ,ntc_trgt_cd      =  #{ntcTrgtCd     } 
    ,ntc_trgt_roleid  =  #{ntcTrgtRoleid } 
    ,updt_dt          =  NOW()           
    ,updtuserid       =  #{user.userid     }
WHERE  popupntcid = #{popupntcid}

mapper.select:
SELECT A.POPUPNTCID popupntcid ,A.TITLE title ,A.CNTNTS cntnts ,A.expsr_lc_cd ,A.MENUID menuid ,A.SITEID sitid ,A.LOGIN_NEED_YN login_need_yn ,A.POPUP_TYPE_CD popup_type_cd ,A.STRT_DT strt_dt ,A.END_DT end_dt ,A.WIDTH_SIZE width_size ,A.VRTCL_SIZE vrtcl_size ,A.TOP_LC top_lc ,A.LEFT_LC left_lc ,A.NTC_TRGT_CD ntc_trgt_cd ,A.ntc_trgt_roleid ntc_trgt_roleid ,CONVERT(A.UPDT_DT, CHAR(10)) updt_dt ,A.UPDTUSERID ,A.REG_DT ,A.REGUSERID ,B.NM upd_nm ,CASE WHEN STRT_DT > CONVERT(NOW(), CHAR(10)) THEN '대기' WHEN END_DT < CONVERT(NOW(), CHAR(10)) THEN '만료' ELSE '진행' END pop_sts FROM TB_CMM_POPUPNTC A, TB_CMM_USER B WHERE 1=1 AND A.UPDTUSERID = B.USERID
AND A.SITEID = #{siteid}
AND A.expsr_lc_cd = #{expsrLcCd}
AND A.TITLE LIKE '%'+#{searchKeyword}+'%'
AND A.CNTNTS LIke '%'+#{searchKeyword}+'%'
AND ( 1=2
or CONVERT(NOW(), CHAR(10)) BETWEEN STRT_DT AND END_DT
or STRT_DT > CONVERT(NOW(), CHAR(10))
or END_DT < CONVERT(NOW(), CHAR(10))
)

SELECT title ,cntnts ,expsr_lc_cd ,menuid ,siteid ,login_need_yn ,popup_type_cd ,strt_dt ,end_dt ,width_size ,vrtcl_size ,top_lc ,left_lc ,ntc_trgt_cd ,ntc_trgt_roleid ,popupntcid FROM TB_CMM_POPUPNTC a WHERE 1=1 AND STRT_DT <= CONVERT(NOW(), CHAR(10)) AND END_DT >= CONVERT(NOW(), CHAR(10)) AND SITEID = #{siteid}
AND expsr_lc_cd = #{expsrLcCd}
AND menuid = #{menuid}
