/* AsgsysSrngDao.selectDsgnAplyList */
SELECT A.PRGRM_NM , A.PRGRMID , A.INST_NM , A.STTS_CD , A.APLY_DT , A.REG_DT , B.SRNG_STTS_CD , C.SRGN_STTS_CD , C.VST_DE , C.VST_HR , C.VST_MNT , CONCAT(C.VST_DE , " " , C.VST_HR , ":" , C.VST_MNT) AS VST_DT FROM TB_ASS_PRGRM A LEFT OUTER JOIN ( SELECT PRGRMID , SRNG_STTS_CD FROM TB_ASS_PRGRM_JDGS LIMIT 0,1 ) B ON A.PRGRMID = B.PRGRMID LEFT OUTER JOIN ( SELECT PRGRMID , SRGN_STTS_CD , VST_DE , VST_HR , VST_MNT FROM TB_ASS_SPRTGRP_SRNG LIMIT 0,1 ) C ON A.PRGRMID = C.PRGRMID LEFT OUTER JOIN TB_CMM_INST D ON A.INSTID = D.INSTID WHERE 1=1
AND A.PRGRM_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.STTS_CD = #{searchSttsCd}
AND A.APLY_DT >= #{searchAplyStartDt}
AND A.APLY_DT < CAST(CONCAT(#{searchAplyEndDt},' 23:59:59') AS DATETIME)
AND B.SRNG_STTS_CD = #{searchSrngSttsCd}
AND C.SRGN_STTS_CD = #{searchSrgnSttsCd }
AND C.VST_DE >= #{searchChckDsgnStartDt}
AND C.VST_DE < CAST(CONCAT(#{searchChckDsgnEndDt},' 23:59:59') AS DATETIME)

