mapper.select:
/* BannerDao.selectBannerList */
SELECT A.BANNERID , A.BANNER_TTL , A.BANNER_FILEID , B.FILE_IDNTFC_KEY , A.EXPSR_YN , A.REG_DT FROM TB_CMM_BANNER A LEFT OUTER JOIN TB_CMM_FILE B ON A.BANNER_FILEID = B.FILEID WHERE 1=1
AND A.SITEID = #{siteid}

/* BannerDao.selectExpsrBannerList */ SELECT A.BANNERID , A.BANNER_TTL , A.BANNER_FILEID , B.FILE_IDNTFC_KEY , A.EXPSR_YN , A.REG_DT FROM TB_CMM_BANNER A LEFT OUTER JOIN TB_CMM_FILE B ON A.BANNER_FILEID = B.FILEID WHERE 1=1 AND A.EXPSR_YN = 'Y'
AND A.SITEID = #{siteid}
ORDER BY ORDR ASC

mapper.update:
/* BannerDao.updateExpsrBanner */
UPDATE TB_CMM_BANNER SET


            ORDR                   = NULL


            ORDR                   = #{ordr}


        , EXPSR_YN                   = #{expsrYn}
        , MDFCN_DT                   = NOW()
        , MDFRID                     = #{user.userid}
 WHERE BANNERID = #{bannerid}

/* BannerDao.updateExpsrBannerSort */

UPDATE TB_CMM_BANNER SET
        ORDR                         = #{item.ordr}
        , MDFCN_DT                   = NOW()
        , MDFRID                     = #{user.userid}
 WHERE BANNERID = #{item.bannerid}

mapper.insert:
/* BannerDao.insertBanner */
INSERT INTO TB_CMM_BANNER (
    SITEID
    , BANNER_TTL
    , BANNER_FILEID
    , URL
    , EXPSR_YN
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{siteid}
    ,#{bannerTtl}
    ,#{bannerFileid}
    ,#{url}
    ,'N'
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* BannerDao.updateBanner */
UPDATE TB_CMM_BANNER SET
        SITEID                       = #{siteid}
        , BANNER_TTL                 = #{bannerTtl}
        , BANNER_FILEID              = #{bannerFileid}
        , URL                        = #{url}
        , MDFCN_DT                   = NOW()
        , MDFRID                     = #{user.userid}
 WHERE BANNERID = #{bannerid}

mapper.delete:
/* BannerDao.updateBanner */
DELETE FROM TB_CMM_BANNER
WHERE BANNERID = #{bannerid}
