    , SBMSN_STTS_CD
    , USER_IP
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
) VALUES (
    #{sbmsnid}
    , #{aplyid}
    , #{formid}
    , #{user.userid}
    , #{vstDt}
    , #{srngOpnn}
    , #{opnn1}
    , #{opnn2}
    , #{opnn3}
    , #{opnn4}
    , #{totScr}
    , #{totalSum}
    , NOW()
    , #{sbmsnSttsCd}
    , #{userIp}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.update:
/** BizAplySrngDaoImpl.updateBizAplySrng **/
UPDATE
    TB_SPB_SRNG_SBMSN
SET
    APLYID = #{aplyid}
    , FORMID = #{formid}
    , USERID = #{user.userid}
    , VST_DT = #{vstDt}
    , SRNG_OPNN = #{srngOpnn}
    , OPNN1 = #{opnn1}
    , OPNN2 = #{opnn2}
    , OPNN3 = #{opnn3}
    , OPNN4 = #{opnn4}
    , TOT_SCR = #{totScr}
    , SCR = #{totalSum}
    , SBMSN_DT = NOW()
    , SBMSN_STTS_CD = #{sbmsnSttsCd}
    , USER_IP = NULL
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE
    SBMSNID = #{sbmsnid}

mapper.insert:
/** BizAplySrngDaoImpl.insertItem **/
INSERT INTO TB_SPB_SRNG_SBMSN_QITEM (
    SBMSNID
    , QITEMID
    , QITEM_SE_CD
    , QITEM
    , ORDR
    , ALTM
    , SCR
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
) VALUES (
    #{sbmsnid}
    , #{qitemid}
    , (SELECT QITEM_SE_CD FROM TB_SPB_SRNG_FORM_QITEM WHERE QITEMID = #{qitemid})
    , (SELECT QITEM FROM TB_SPB_SRNG_FORM_QITEM WHERE QITEMID = #{qitemid})
    , (SELECT ORDR FROM TB_SPB_SRNG_FORM_QITEM WHERE QITEMID = #{qitemid})
    , (SELECT ALTM FROM TB_SPB_SRNG_FORM_QITEM WHERE QITEMID = #{qitemid})
    , #{scr}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.update:
/** BizAplySrngDaoImpl.updateItem **/
UPDATE
    KEEP_DEV01.TB_SPB_SRNG_SBMSN_QITEM
SET
    SCR = #{scr}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE
    SBMSNID = #{sbmsnid}
    AND QITEMID = #{qitemid}
