mapper.select:
/* BookDao.selectBookList */
SELECT A.BOOKID , A.TTL , CONCAT(B2.CD_NM, ' > ', B1.CD_NM) AS EDUSBJCTCD , C1.CD_NM AS EDUTRGTCD , A.PLSCMPN , A.RPRS_IMG_FILEID , E.FILEGRPID , NVL(A.HITS,0) AS HITS , A.REG_DT FROM TB_CMM_BOOK A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID WHERE 1=1
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%') AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND B2.CD = #{searchManinEduSbjctCd}
AND B1.CD = #{searchMdleEduSbjctCd}
AND C1.CD = #{searchEduTrgtCd}
AND A.PLSCMPN LIKE CONCAT('%',#{searchPlscmpn},'%')
AND DATE(A.REG_DT) >= #{startDt}
AND DATE(A.REG_DT) <= #{endDt}

/* BookDao.selectBookInfo */ SELECT A.BOOKID , A.EDU_SBJCT_CD , B2.CD AS MAIN_EDU_SBJCT_CD , C1.CD AS EDU_TRGT_CD , A.WRITR , A.WRITR_PICTR , A.PLSCMPN , A.AMT , A.TTL , A.CN , DATE(A.REG_DT) AS REG_DT , A.RGTRID , CONCAT(F.ACNT,'(',F.NM,')') AS rgtridNm , A.RPRS_IMG_FILEID , E.FILEGRPID , E.FILE_IDNTFC_KEY , E.ORGINL_FILE_NM , A.ATCH_FILEGRPID FROM TB_CMM_BOOK A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID
LEFT OUTER JOIN TB_CMM_USER F ON A.RGTRID = F.USERID WHERE BOOKID = #{bookid}

mapper.insert:
/* BookDao.insertBook */
INSERT INTO TB_CMM_BOOK (
    EDU_SBJCT_CD
    , EDU_TRGT_CD
    , WRITR
    , WRITR_PICTR
    , PLSCMPN
    , AMT
    , TTL
    , CN
    , RPRS_IMG_FILEID
    , ATCH_FILEGRPID
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{eduSbjctCd}
    ,#{eduTrgtCd}
    ,#{writr}
    ,#{writrPictr}
    ,#{plscmpn}
    ,#{amt}
    ,#{ttl}
    ,#{cn}
    ,#{rprsImgFileid}
    ,#{atchFilegrpid}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* BookDao.updateBook */
 UPDATE TB_CMM_BOOK SET
     EDU_SBJCT_CD      = #{eduSbjctCd}
     ,EDU_TRGT_CD      = #{eduTrgtCd}
     ,WRITR            = #{writr}
     ,WRITR_PICTR      = #{writrPictr}
     ,PLSCMPN          = #{plscmpn}
     ,AMT              = #{amt}
     ,TTL              = #{ttl}
     ,CN               = #{cn}
     ,RPRS_IMG_FILEID  = #{rprsImgFileid}
     ,ATCH_FILEGRPID   = #{atchFilegrpid}
     ,MDFCN_DT         = NOW()
     ,MDFRID           = #{user.userid}
  WHERE BOOKID = #{bookid}

