mapper.select:
/* CntntsDao.selectCntntsList */
SELECT A.CNTNTSID , A.TTL , CONCAT(B2.CD_NM, ' > ', B1.CD_NM) AS EDUSBJCTCD , C1.CD_NM AS EDUTRGTCD , CONCAT(D2.CD_NM, ' > ', D1.CD_NM) AS TYPECD , A.RPRS_IMG_FILEID , E.FILEGRPID , A.REG_DT FROM TB_CMM_CNTNTS A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD LEFT OUTER JOIN TB_CMM_CD D1 ON A.TYPE_CD = D1.CD LEFT OUTER JOIN TB_CMM_CD D2 ON D1.UPPR_CD = D2.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID WHERE 1=1
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%') AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND B2.CD = #{searchManinEduSbjctCd}
AND B1.CD = #{searchMdleEduSbjctCd}
AND C1.CD = #{searchEduTrgtCd}
AND D2.CD = #{searchMainTypeCd}
AND D1.CD = #{searchMdleTypeCd}
AND DATE(A.REG_DT) >= #{startDt}
AND DATE(A.REG_DT) <= #{endDt}

/* CntntsDao.selectCntntsInfo */ SELECT A.CNTNTSID , A.TTL , A.EDU_SBJCT_CD , B2.CD AS MAIN_EDU_SBJCT_CD , C1.CD AS EDU_TRGT_CD , A.TYPE_CD , D2.CD AS MAIN_TYPE_CD , A.ORIGIN , A.MNFCT_YY , A.PLY_SECND , A.TTL , A.CN , DATE(A.REG_DT) AS REG_DT , A.RGTRID , CONCAT(F.ACNT,'(',F.NM,')') AS rgtridNm , A.RPRS_IMG_FILEID , E.FILEGRPID , E.FILE_IDNTFC_KEY , E.ORGINL_FILE_NM , A.ATCH_FILEGRPID FROM TB_CMM_CNTNTS A LEFT OUTER JOIN TB_CMM_CD B1 ON A.EDU_SBJCT_CD = B1.CD LEFT OUTER JOIN TB_CMM_CD B2 ON B1.UPPR_CD = B2.CD LEFT OUTER JOIN TB_CMM_CD C1 ON A.EDU_TRGT_CD = C1.CD LEFT OUTER JOIN TB_CMM_CD D1 ON A.TYPE_CD = D1.CD LEFT OUTER JOIN TB_CMM_CD D2 ON D1.UPPR_CD = D2.CD
LEFT OUTER JOIN TB_CMM_FILE E ON A.RPRS_IMG_FILEID = E.FILEID
LEFT OUTER JOIN TB_CMM_USER F ON A.RGTRID = F.USERID WHERE CNTNTSID = #{cntntsid}

mapper.insert:
/* CntntsDao.insertCntnts */
INSERT INTO TB_CMM_CNTNTS (
    EDU_SBJCT_CD
    , TYPE_CD
    , EDU_TRGT_CD
    , ORIGIN
    , MNFCT_YY
    , PLY_SECND
    , TTL
    , CN
    , RPRS_IMG_FILEID
    , ATCH_FILEGRPID
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{eduSbjctCd}
    ,#{typeCd}
    ,#{eduTrgtCd}
    ,#{origin}
    ,#{mnfctYy}
    ,#{plySecnd}
    ,#{ttl}
    ,#{cn}
    ,#{rprsImgFileid}
    ,#{atchFilegrpid}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* CntntsDao.updateCntnts */
 UPDATE TB_CMM_CNTNTS SET
     EDU_SBJCT_CD      = #{eduSbjctCd}
     ,TYPE_CD          = #{typeCd}
     ,EDU_TRGT_CD      = #{eduTrgtCd}
     ,ORIGIN           = #{origin}
     ,MNFCT_YY         = #{mnfctYy}
     ,PLY_SECND        = #{plySecnd}
     ,TTL              = #{ttl}
     ,CN               = #{cn}
     ,RPRS_IMG_FILEID  = #{rprsImgFileid}
     ,ATCH_FILEGRPID   = #{atchFilegrpid}
     ,MDFCN_DT         = NOW()
     ,MDFRID           = #{user.userid}
  WHERE CNTNTSID = #{cntntsid}

mapper.delete:
/* CntntsDao.deleteCntnts */
DELETE FROM TB_CMM_CNTNTS
WHERE CNTNTSID IN 

