mapper.update:
UPDATE TB_CMM_CNTST SET
    TTL = #{ttl}
    , THMBN_FILEID = #{thmbnFileid}
    , ATCH_FILEGRPID = #{atchFilegrpid}
    , APLY_BGNG_DT = #{aplyBgngDt}
    , APLY_END_DT = #{aplyEndDt}
    , PRSNTN_DT = #{prsntnDt}
    , DPCN_PSBLTY_YN = #{dpcnPsbltyYn}
    , CLSF_CD = #{clsfCd}
    , GDNC = #{gdnc}
    , TRMS = #{trms}
    , CN = #{cn}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE CNTSTID = #{cntstid}

mapper.select:
SELECT CC.CNTSTID , CC.TTL , CC.THMBN_FILEID , CC.ATCH_FILEGRPID , CC.APLY_BGNG_DT , CC.APLY_END_DT , CC.PRSNTN_DT , CC.DPCN_PSBLTY_YN , CC.CLSF_CD , CC.GDNC , CC.TRMS , CC.CN , CC.MDFCN_DT , CC.MDFRID , CC.REG_DT , CC.RGTRID , CU.NM RGTR_NM , CU.ACNT RGTR_ACNT , (CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d') < DATE_FORMAT(CC.APLY_BGNG_DT, '%Y%m%d') THEN '159101' WHEN DATE_FORMAT(NOW(), '%Y%m%d') > DATE_FORMAT(CC.APLY_END_DT, '%Y%m%d') THEN '159103' ELSE '159102' END) CNTST_STTS_CD FROM TB_CMM_CNTST CC INNER JOIN TB_CMM_USER CU ON CU.USERID = CC.RGTRID WHERE 1=1
AND CC.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND CC.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND ( CC.TTL LIKE CONCAT('%',#{searchKeyword},'%') OR CC.CN LIKE CONCAT('%',#{searchKeyword},'%') )
AND CC.CLSF_CD = #{searchCntstClsfCd}
AND (CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d') < DATE_FORMAT(CC.APLY_BGNG_DT, '%Y%m%d') THEN '159101' WHEN DATE_FORMAT(NOW(), '%Y%m%d') > DATE_FORMAT(CC.APLY_END_DT, '%Y%m%d') THEN '159103' ELSE '159102' END) = #{searchCntstSttsCd}

SELECT FLD_CD
FROM TB_CMM_CNTST_FLD_MAPNG
WHERE CNTSTID = #{cntstId}
