mapper.select:
/* DsgnPrgrmDao.selectDsgnPrgrmList */
SELECT DISTINCT A.PRGRMID , Z.HSTRYID , A.INSTID , A.DSGN_CYCL , A.DSGN_NO , A.PRGRM_NM , A.INST_NM , ( SELECT STTS_CD FROM TB_ASS_DSGN_HSTRY WHERE PRGRMID = A.PRGRMID AND HSTRYID = Z.HSTRYID) AS STTS_CD , B.CHG_APLY_CNT , C.OPER_RSLT_CNT , D.IMPLMNT_IDNTY_CNT , E.OBJC_INFO_CNT , A.DSGN_BGNG_DE , A.DSGN_END_DE , CONCAT(A.DSGN_BGNG_DE, " ~ ", A.DSGN_END_DE ) AS DSGN_PRD FROM TB_ASS_PRGRM A INNER JOIN /*지정 이력*/ ( SELECT ZZ.PRGRMID , MAX(ZZ.HSTRYID) AS HSTRYID FROM TB_ASS_DSGN_HSTRY ZZ WHERE 1=1 GROUP BY ZZ.PRGRMID ) Z ON A.PRGRMID = Z.PRGRMID LEFT OUTER JOIN /*운영결과차수*/ ( SELECT BB.PRGRMID , BB.APLYID , CONCAT(COUNT(CASE WHEN BB.APLY_STTS_CD = '119104' THEN 1 END), "/", COUNT(BB.APLYID)) AS CHG_APLY_CNT FROM TB_ASS_CHG_APLY BB GROUP BY BB.PRGRMID ) B ON A.PRGRMID = B.PRGRMID LEFT OUTER JOIN /*운영결과차수*/ ( SELECT CC.PRGRMID , CC.STTS_CD , CONCAT(COUNT(CASE WHEN CC.STTS_CD = '128103' THEN 1 END), "/", COUNT(CC.CYCLID )) AS OPER_RSLT_CNT FROM TB_ASS_OPER_RSLT_CYCL CC GROUP BY CC.PRGRMID ) C ON A.PRGRMID =C.PRGRMID LEFT OUTER JOIN ( SELECT AA.PRGRMID , AA.CYCLID , CC.SRNGID , CONCAT(COUNT(CASE WHEN CC.FILEGRPID IS NOT NULL THEN 0 END), "/", COUNT(BB.CYCLID)) AS IMPLMNT_IDNTY_CNT FROM TB_ASS_OPER_RSLT_CYCL AA LEFT OUTER JOIN TB_ASS_OPER_RSLT BB ON AA.CYCLID = BB.CYCLID LEFT OUTER JOIN TB_ASS_IMPLMNT_IDNTY_SRNG CC ON AA.CYCLID =CC.CYCLID ) D ON A.PRGRMID = D.PRGRMID LEFT OUTER JOIN ( SELECT AA.PRGRMID , CONCAT(COUNT(AA.APLYID), "/", COUNT(CASE WHEN AA.APLY_STTS_CD = '129101' THEN 1 END)) AS OBJC_INFO_CNT FROM TB_ASS_OBJC_APLY AA GROUP BY AA.PRGRMID ) E ON A.PRGRMID = E.PRGRMID WHERE 1=1
AND A.PRGRM_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.STTS_CD = #{searchSttsCd}
AND A.A.DSGN_BGNG_DE >= #{searchStartDsgnDe}
AND A.DSGN_END_DE < CAST(CONCAT(#{searchEndDsgnDe},' 23:59:59') AS DATETIME)
AND A.DSGN_CYCL = #{searchDsgnCycl}

mapper.update:
UPDATE TB_ASS_OBJC_APLY
SET
ANS = #{ans}
, APLY_STTS_CD = '129102'
, PICID = #{user.userid}
, MDFCN_DT = NOW()
, MDFRID = #{user.userid}

    , ANS_DT = DATE_FORMAT(NOW(),'%Y-%m-%d')

WHERE APLYID = #{aplyid}

