mapper.insert:
/* EduSareaDao.insertEduSarea */
INSERT INTO TB_EDU_SAREA (
    SAREA_NM
  , INSTID
  , APLY_LMT_CNT
  , EXPLN
  , USE_YN
  , MDFCN_DT
  , MDFRID
  , REG_DT
  , RGTRID
)VALUES(
    #{sareaNm}
  , #{cnsgnInstId}
  , #{aplyLmtCnt}
  , #{expln}
  , #{useYn}
  , NOW()
  , #{user.userid}
  , NOW()
  , #{user.userid}
)

mapper.update:
/* EduSareaDao.updateEduSarea */
UPDATE TB_EDU_SAREA SET
        SAREA_NM                 = #{sareaNm}
        ,INSTID                  = #{cnsgnInstId}
        ,APLY_LMT_CNT            = #{aplyLmtCnt}
        ,EXPLN                   = #{expln}
        ,USE_YN                  = #{useYn} 
        ,MDFCN_DT                = NOW() 
        ,MDFRID                  = #{user.userid}
 WHERE SAREAID                  = #{sareaId}

mapper.insert:
/* EduSareaDao.deleteCtprvnCd */
DELETE FROM TB_EDU_SAREA_CTPRVN_MAPNG
WHERE 1=1
  AND SAREAID = #{sareaId}

