mapper.select:
/* EnvWordDao.selectEnvWordList */
SELECT A.WORDID , A.WORD , A.WORD_ENGL , A.REG_DT FROM TB_CMM_ENV_WORD A WHERE 1=1
AND OXPR_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND CLSF_CD = #{searchCd}

mapper.insert:
/* EnvWordDao.insertEnvWord */
INSERT INTO TB_CMM_ENV_WORD (
  WORD
  , WORD_ENGL
  , CN_SUMRY
  , CN
  , MDFCN_DT
  , MDFRID
  , REG_DT
  , RGTRID
  )VALUES(
    #{word}
    ,#{wordEngl}
    ,#{cnSumry}
    ,#{cn}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
  )

mapper.update:
/* EnvWordDao.updateEnvWord */
UPDATE TB_CMM_ENV_WORD SET
        WORD                    = #{word}
        ,WORD_ENGL              = #{wordEngl} 
        ,CN_SUMRY              = #{cnSumry} 
        ,CN                     = #{cn}
        ,MDFCN_DT               = NOW() 
        ,MDFRID                 = #{user.userid}
 WHERE WORDID = #{wordid}

mapper.delete:
        /* EnvWordDao.deleteEnvWord */
		DELETE FROM TB_CMM_ENV_WORD 
        WHERE WORDID IN 

        #{item}
