mapper.select:
/* EnveduDao.selectEnveduList */
SELECT A.ENVEDUID , A.CLSF_CD , A.ENVEDU_SE_CD , CONCAT(A.YR, '-' ,A.MM) AS YRMM , A.YR , A.MM , A.QU , A.TTL , A.TYPE_CD , A.URL , A.THMBN_FILEID , C.FILEGRPID , A.CN_STYLE , A.CN , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID , B.NM FROM TB_CMM_WBZN_ENVEDU A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID WHERE 1=1
AND A.CLSF_CD = #{clsfCd}
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.ENVEDU_SE_CD = #{searchSeCd}
AND A.YR = #{searchYr}
AND A.MM = #{searchMm}

mapper.insert:
/* EnveduDao.insertEnvedu */
INSERT INTO TB_CMM_WBZN_ENVEDU (
    CLSF_CD
    , ENVEDU_SE_CD
    , YR
    , MM
    , QU
    , TTL
    , TYPE_CD

    , URL

    , THMBN_FILEID

    , CN_STYLE


    , CN

    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{clsfCd}
    ,#{enveduSeCd}
    ,#{yr}
    ,#{mm}
    ,#{qu}
    ,#{ttl}
    ,#{typeCd}

    ,#{url}

    ,#{thmbnFileid}

    ,#{cnStyle}


    ,#{cn}

    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* EnveduDao.updateEnvedu */
UPDATE TB_CMM_WBZN_ENVEDU SET
        ENVEDU_SE_CD            = #{enveduSeCd}
        ,YR                     = #{yr} 
        ,MM                     = #{mm}
        ,QU                     = #{qu}
        ,TTL                    = #{ttl}
        ,TYPE_CD                = #{typeCd} 
        ,THMBN_FILEID           = #{thmbnFileid}

        ,url                    = #{url}


        ,CN_STYLE               = #{cnStyle}


        ,CN                     = #{cn}

        ,MDFCN_DT               = NOW() 
        ,MDFRID                 = #{user.userid}
 WHERE ENVEDUID = #{enveduid}
