mapper.select:
SELECT A.DTAID , A.TTL , A.CN , A.NATION_CD , B.CD_NM NATION_NM , A.HMPG , A.PSTN , A.CNTCT , A.OPNHR , A.ETRFEE , A.EXPRN_PRGRM , A.CPYRHT_CD , A.ATCH_FILEGRPID , A.HITS , A.REG_DT , A.RGTRID FROM TB_CMM_ETC_DTA A LEFT OUTER JOIN TB_CMM_CD B ON A.NATION_CD = B.CD WHERE 1=1
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.CN LIKE CONCAT('%',#{searchKeyword},'%')
AND A.NATION_CD LIKE CONCAT('%',#{searchNationCd},'%')
AND DATE(A.REG_DT) >= #{searchBgngDt}
AND DATE(A.REG_DT) <= #{searchEndDt}

mapper.insert:
INSERT INTO TB_CMM_ETC_DTA(
    TTL
    , CN
    , NATION_CD
    , HMPG
    , PSTN
    , CNTCT
    , OPNHR
    , ETRFEE
    , EXPRN_PRGRM
    , CPYRHT_CD
    , ATCH_FILEGRPID
    , HITS
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)
VALUES(
    #{ttl}
    , #{cn}
    , #{nationCd}
    , #{hmpg}
    , #{pstn}
    , #{cntct}
    , #{opnhr}
    , #{etrfee}
    , #{exprnPrgrm}
    , #{cpyrhtCd}
    , #{atchFilegrpid}
    , 0
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.update:
UPDATE TB_CMM_ETC_DTA
SET
    TTL = #{ttl}
    , CN = #{cn}
    , NATION_CD = #{nationCd}
    , HMPG = #{hmpg}
    , PSTN = #{pstn}
    , CNTCT = #{cntct}
    , OPNHR = #{opnhr}
    , ETRFEE = #{etrfee}
    , EXPRN_PRGRM = #{exprnPrgrm}
    , CPYRHT_CD = #{cpyrhtCd}
    , ATCH_FILEGRPID = #{atchFilegrpid}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE DTAID = #{dtaid}
