mapper.select:
SELECT A.USERID , A.EXPRT_TYPE_CD , A.FLD_LCTR_YN , A.FLD_PLANNG_YN , A.FLD_CNSLTNG_YN , A.FLD_ETC_YN , A.FLD_ETC_CN , A.MOBLPHON_RLS_YN , A.EML_RLS_YN , A.QLFC_RLS_YN , A.HDOF_RLS_YN , A.CAREER_RLS_YN , A.ENT_LCTR_DMND_RCPTN_YN , A.LCTR_GDNC_RCPTN_YN , A.ENV_EDU_CAREER_YY , A.ENV_EDU_CAREER_MM , A.STTS_CD , A.APLY_DT , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID , A.APRV_DT , A.RJCT_DT , A.CNCL_DT , B.STTS_CD AS USER_STTS_CD , B.ACNT , B.NM , GROUP_CONCAT(D.CD_NM) AS EXPRT_SBJCT , GROUP_CONCAT(C.CD_NM) AS EXPRT_ACTVT_SCOPE , GROUP_CONCAT(E.CD_NM) AS EXPRT_TRGT FROM TB_ASS_EXPRT A INNER JOIN TB_CMM_USER B ON A.USERID = B.USERID
LEFT OUTER JOIN ( SELECT A.USERID, B.CD_NM, B.CD FROM TB_ASS_EXPRT_ACTVT_SCOPE A INNER JOIN TB_CMM_CD B ON A.RGN_CD = B.CD ) C ON A.USERID = C.USERID
LEFT OUTER JOIN ( SELECT A.USERID, B.CD_NM, B.CD FROM TB_ASS_EXPRT_SBJCT A INNER JOIN TB_CMM_CD B ON A.SBJCT_CD = B.CD ) D ON A.USERID= D.USERID
LEFT OUTER JOIN ( SELECT A.USERID, B.CD_NM, B.CD FROM TB_ASS_EXPRT_TRGT A INNER JOIN TB_CMM_CD B ON A.TRGT_CD = B.CD ) E ON A.USERID= E.USERID
WHERE 1=1
AND B.NM LIKE CONCAT('%',#{searchKeyword},'%')
AND B.ACNT LIKE CONCAT('%',#{searchKeyword},'%')
AND A.EXPRT_TYPE_CD = #{exprtTypeCd}
AND A.FLD_LCTR_YN = 'Y'
AND A.FLD_PLANNG_YN = 'Y'
AND A.FLD_CNSLTNG_YN = 'Y'
AND A.FLD_ETC_YN = 'Y'
AND E.CD = #{exprtTrgt}
AND D.CD = #{exprtSbjct}
AND C.CD = #{exprtActvtScope}
AND B.STTS_CD = #{userSttsCd}
AND A.STTS_CD = #{sttsCd}
GROUP BY A.USERID

mapper.update:
UPDATE TB_ASS_EXPRT SET STTS_CD = #{sttsCd}
, RJCT_DT = NULL
, APRV_DT = IFNULL(APRV_DT, NOW()) , CNCL_DT = NULL
, RJCT_DT = NOW()
, CNCL_DT = NOW()
WHERE USERID = #{userid}

mapper.insert:
INSERT INTO TB_ASS_EXPRT_LOG (
USERID
, PRCS_SE_CD
, RSN_CN
, REG_DT
, RGTRID

, MDFCN_DMND_ID

) VALUES (
#{userid}
, #{prcsSeCd}
, #{rsnCn}
, NOW()
, #{user.userid}

, #{mdfcnDmndId}

)

mapper.select:
SELECT TRUNCATE(AVG(SCR),1)
FROM TB_ASS_LCTR_DMND
WHERE EXPRTID = #{userid}
