mapper.select:
/* FaqDao.getList */
SELECT A.CLID, A.USER_SE_CD, A.FAQID, A.TITLE, A.CNTNTS, A.ORD as ord, A.USE_YN, A.UPDT_DT, CONVERT(A.REG_DT, CHAR(10)) AS REG_DT, B.CL_NM cl_nm FROM TB_FAQ A, TB_FAQ_CL B WHERE 1=1 AND A.CLID = B.CLID
AND A.CLID = #{clid}
AND A.USE_YN = #{use_yn}
AND A.USER_SE_CD = #{user_se_cd}
AND A.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CNTNTS LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.insert:
/* FaqDao.addFaq */
SELECT IFNULL (Max(ORD),0) +1 FROM TB_FAQ WHERE 1=1
AND USER_SE_CD = #{user_se_cd}
INSERT INTO TB_FAQ (CLID, TITLE, CNTNTS, USER_SE_CD, ORD, USE_YN, UPDT_DT, REG_DT,UPDTUSERID, REGUSERID) VALUES (#{clid}, #{title}, #{cntnts}, #{user_se_cd}, #{ord}, #{use_yn}, NOW(), NOW(), #{user.userid} ,#{reguserid})

mapper.delete:
    /* FaqDao.deleteFaq */
	DELETE FROM TB_FAQ 
	WHERE 1=1
	  AND FAQID = #{faqid}

mapper.update:
	    /* FaqDao.modifyFaqOrdUp */
     UPDATE TB_FAQ
         SET 
         ORD = ORD + 1
     WHERE 
         ORD   = #{ord}
         AND USER_SE_CD  = #{user_se_cd}

/* FaqDao.modifyFaqOrdDown */
UPDATE TB_FAQ
    SET 
    ORD = ORD - 1
WHERE 
    ORD   = #{ord} 
    AND USER_SE_CD  = #{user_se_cd}

/* FaqDao.modifyFaqOrdByfaqid */
UPDATE TB_FAQ
    SET 
    ORD =  #{ord}
WHERE 
    FAQID = #{faqid}
