mapper.select:
/* InstDao.selectInstList */
SELECT A.INSTID ,A.INST_NM ,A.INST_CD ,A.INST_TYPE_CD ,A.BRNO ,A.RPRSV_NM ,A.TELNO ,A.FXNO ,A.ZIP ,A.ADDR ,A.ADDR_DTL ,A.HMPG ,A.LOGO_FILEID ,A.FILEGRPID ,A.APRV_STTS_CD ,A.USE_YN ,A.MDFCN_DT ,A.MDFRID ,A.REG_DT ,A.RGTRID ,B.MNGNMS FROM TB_CMM_INST A LEFT OUTER JOIN( SELECT INSTID, group_concat(NM) MNGNMS FROM TB_CMM_USER WHERE INSTID IS NOT NULL GROUP BY INSTID ) B ON A.INSTID = B.INSTID WHERE 1=1
AND A.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.RPRSV_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND B.MNGNMS LIKE CONCAT('%',#{searchKeyword},'%')
AND A.BRNO LIKE CONCAT('%',#{searchKeyword},'%')
AND A.INST_TYPE_CD = #{searchInstCd}
AND A.APRV_STTS_CD = #{searchApprovalSttsCd}

mapper.update:
/* InstDao.updateInst */
 UPDATE TB_CMM_INST SET
     INST_NM       = #{instNm      }       
     ,INST_CD      = #{instCd      }       
     ,INST_TYPE_CD = #{instTypeCd  }          
     ,RGN_CD       = #{rgnCd       }       
     ,BRNO         = #{brno        }      
     ,RPRSV_NM     = #{rprsvNm     }       
     ,TELNO        = #{telno       }      
     ,FXNO         = #{fxno        }      
     ,ZIP          = #{zip         }      
     ,ADDR         = #{addr        }      
     ,ADDR_DTL     = #{addrDtl     }       
     ,HMPG         = #{hmpg        }      
     ,LOGO_FILEID  = #{logoFileid  }     
     ,FILEGRPID    = #{filegrpid   }       
     ,APRV_STTS_CD = #{aprvSttsCd  }        
     ,USE_YN       = #{useYn       }       
     ,MDFCN_DT     = NOW()                 
     ,MDFRID       = #{user.userid }        
     ,REG_DT       = NOW()                 
     ,RGTRID       = #{user.userid }        
  WHERE INSTID = #{instid}

mapper.select:
/* InstDao.selectManagerList */
