mapper.select:
/* MvmnAplyDao.selectMvmnAplyList */
SELECT Z.PRGRM_SCHDLID , Z.EDU_YR , Z.EDU_MM , Z.PRGRMID , Z.PRGRM_NM , Z.EDU_NOPE , Z.SAREAID , Z.RCPT_MTHD_NM , Z.SAREA_NM , SUM(Z.ORG_WTNG_APLY_CNT) WTNG_APLY_CNT , SUM(Z.ORG_WTNG_APPRV_CNT) WTNG_APPRV_CNT , SUM(Z.ORG_APLY_CMPLT_CNT) APLY_CMPLT_CNT , SUM(Z.ORG_CNCL_APLY_CNT) CNCL_APLY_CNT , SUM(Z.ORG_DNL_APPRV_CNT) DNL_APPRV_CNT , Z.REG_DT FROM ( SELECT C.PRGRM_SCHDLID , SUBSTRING_INDEX(C.YM, '-', 1) EDU_YR , CONCAT(REPLACE(SUBSTRING_INDEX(C.YM, '-', -1), '0', ''), '월') EDU_MM , C.YM , D.PRGRMID , E.PRGRM_NM , E.EDU_NOPE , C.SAREAID , L.CD_NM AS RCPT_MTHD_NM , F.SAREA_NM , (SELECT COUNT(T1.STTS_CD) FROM TB_EDU_MVMN_PRGRM_APLY T1 WHERE T1.STTS_CD = '180101' AND T1.TME_SCHDLID = B.TME_SCHDLID) ORG_WTNG_APLY_CNT , (SELECT COUNT(T2.STTS_CD) FROM TB_EDU_MVMN_PRGRM_APLY T2 WHERE T2.STTS_CD = '180102' AND T2.TME_SCHDLID = B.TME_SCHDLID) ORG_WTNG_APPRV_CNT , (SELECT COUNT(T3.STTS_CD) FROM TB_EDU_MVMN_PRGRM_APLY T3 WHERE T3.STTS_CD = '180103' AND T3.TME_SCHDLID = B.TME_SCHDLID) ORG_APLY_CMPLT_CNT , (SELECT COUNT(T4.STTS_CD) FROM TB_EDU_MVMN_PRGRM_APLY T4 WHERE T4.STTS_CD = '180104' AND T4.TME_SCHDLID = B.TME_SCHDLID) ORG_CNCL_APLY_CNT , (SELECT COUNT(T5.STTS_CD) FROM TB_EDU_MVMN_PRGRM_APLY T5 WHERE T5.STTS_CD = '180105' AND T5.TME_SCHDLID = B.TME_SCHDLID) ORG_DNL_APPRV_CNT , C.YM REG_DT FROM TB_EDU_MVMN_PRGRM_SCHDL C LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME_SCHDL B ON B.PRGRM_SCHDLID = C.PRGRM_SCHDLID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_SCHDL_PRGRM D ON D.PRGRM_SCHDLID = C.PRGRM_SCHDLID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM E ON E.PRGRMID = D.PRGRMID LEFT OUTER JOIN TB_EDU_SAREA F ON F.SAREAID = C.SAREAID LEFT OUTER JOIN TB_CMM_CD L ON L.CD = E.RCPT_MTHD_CD WHERE 1=1
AND E.PRGRM_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND SUBSTRING_INDEX(C.YM, '-', 1) = #{searchEduYr}
AND C.SAREAID = #{searchSareaId}
AND SUBSTRING_INDEX(C.YM, '-', -1) = #{searchSareaMm}
AND E.RCPT_MTHD_CD = #{searchRcptMthdCd}
GROUP BY C.PRGRM_SCHDLID, C.YM, B.TME_SCHDLID, D.PRGRMID, C.SAREAID )Z GROUP BY Z.PRGRM_SCHDLID, Z.YM, Z.PRGRMID, Z.SAREAID

/* MvmnAplyDao.mvmnAplyExcelDownList */ SELECT A.APLYID , B.PRGRM_SCHDLID , B.DE , C.TME_NM , A.REG_DT AS RCPT_DT , A.INSTID , E.INST_NM , A.USERID APLY_USERID , F.NM APLY_USER_NM , A.TME_SCHDLID , A.ZIP , A.ADDR , A.ADDR_DTL , A.SIGNGU_CD , CONCAT((SELECT TA.CD_NM FROM TB_CMM_CD TA WHERE TA.CD = G.UPPR_CD), ' > ', G.CD_NM) AS SIGNGU_DESC , A.TELNO , F.MOBLPHON , A.EML , A.EDU_NOPE , A.MEMO , A.STTS_CD , D.CD_NM AS STTS_NM , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID FROM TB_EDU_MVMN_PRGRM_APLY A LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME_SCHDL B ON B.TME_SCHDLID = A.TME_SCHDLID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME C ON C.TMEID = B.TMEID LEFT OUTER JOIN TB_CMM_CD D ON D.CD = A.STTS_CD LEFT OUTER JOIN TB_CMM_INST E ON E.INSTID = A.INSTID LEFT OUTER JOIN TB_CMM_USER F ON F.USERID = A.USERID LEFT OUTER JOIN TB_CMM_CD G ON G.CD = A.SIGNGU_CD WHERE B.PRGRM_SCHDLID = #{prgrmSchdlid}
AND (E.INST_NM LIKE CONCAT('%',#{searchKeyword},'%') OR A.USERID LIKE CONCAT('%',#{searchKeyword},'%') OR F.NM LIKE CONCAT('%',#{searchKeyword},'%') OR F.MOBLPHON LIKE CONCAT('%',#{searchKeyword},'%'))
AND E.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.USERID LIKE CONCAT('%',#{searchKeyword},'%')
AND F.NM LIKE CONCAT('%',#{searchKeyword},'%')
AND F.MOBLPHON LIKE CONCAT('%',#{searchKeyword},'%')
AND B.DE >= #{searchDeStartDt}
AND B.DE < CAST(CONCAT(#{searchDeEndDt},' 23:59:59') AS DATETIME)
AND A.REG_DT >= #{searchRcptStartDt}
AND A.REG_DT < CAST(CONCAT(#{searchRcptEndDt},' 23:59:59') AS DATETIME)

mapper.insert:
/* MvmnAplyDao.insertMvmnAply */
INSERT INTO TB_EDU_MVMN_PRGRM_APLY (
    INSTID
    , USERID
    , ZIP
    , ADDR
    , ADDR_DTL
    , SIGNGU_CD
    , TELNO
    , FXNO
    , EML
    , EDU_NOPE
    , RMRK
    , MEMO
    , STTS_CD
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)
SELECT 
    #{instid}
    ,#{aplyUserid}
    ,#{zip}
    ,#{addr}
    ,#{addrDtl}
    ,#{signguCd}
    ,#{telno}
    ,#{fxno}
    ,#{eml}
    ,#{eduNope}
    ,#{rmrk}
    ,#{memo}
    -- ,CASE WHEN C.RCPT_MTHD_CD = '156101' THEN '180102' ELSE CASE WHEN COUNT(D.STTS_CD) <= C.EDU_NOPE THEN '180102' ELSE '180101' END END
    ,#{sttsCd}    
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
  FROM TB_EDU_MVMN_PRGRM_TME_SCHDL A
  LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_TME B ON B.TMEID = A.TMEID
  LEFT OUTER JOIN TB_EDU_MVMN_PRGRM C ON C.PRGRMID = B.PRGRMID                
  LEFT OUTER JOIN (
    SELECT T1.STTS_CD, T2.TME_SCHDLID 
    FROM TB_EDU_MVMN_PRGRM_APLY T1
       , TB_EDU_MVMN_PRGRM_APLY_SCHDL T2 
    WHERE T1.APLYID = T2.APLYID) D ON D.TME_SCHDLID = #{tmeSchdlid}                 
 WHERE A.TME_SCHDLID = #{tmeSchdlid}

mapper.update:
/* MvmnAplyDao.updateMvmnAply */
UPDATE TB_EDU_MVMN_PRGRM_APLY SET
        INSTID                      = #{instid}
        , USERID                    = #{aplyUserid}
        , ZIP                       = #{zip}
        , ADDR                      = #{addr}
        , ADDR_DTL                  = #{addrDtl}
        , TELNO                     = #{telno}
        , FXNO                      = #{fxno}
        , EML                       = #{eml}
        , EDU_NOPE                  = #{eduNope}
        , RMRK                      = #{rmrk}
        , MEMO                      = #{memo}
        , STTS_CD                   = #{sttsCd}
        , MDFCN_DT                  = NOW()
        , MDFRID                    = #{user.userid}
 WHERE APLYID                       = #{aplyId}

/* MvmnAplyDao.updateSttsCdMvmnAply */
UPDATE TB_EDU_MVMN_PRGRM_APLY SET
        STTS_CD                    = #{sttsCd}
        , MDFCN_DT                 = NOW()
        , MDFRID                   = #{user.userid}
 WHERE APLYID                      = #{aplyId}

/* MvmnAplyDao.updateMvmnAplyClose */
UPDATE TB_EDU_MVMN_PRGRM_TME SET
        USE_YN                     = #{useYn}
        , MDFCN_DT                 = NOW()
        , MDFRID                   = #{user.userid}
 WHERE TMEID                      = #{tmeId}

mapper.insert:
/* MvmnAplyDao.deleteTrgtCd */
DELETE FROM TB_EDU_MVMN_PRGRM_APLY_EDU_TRGT
WHERE 1=1
  AND APLYID = #{aplyId}
