mapper.select:
/* MvmnPrgrmDao.selectMvmnPrgrmList */
SELECT A.PRGRMID , A.SAREAID , B.SAREA_NM , A.EDU_YR , A.EDU_SE_CD , F.CD_NM EDU_SE_NM , A.SAREA_CD , A.EDU_TYPE_CD , A.PRGRM_NM , CONCAT((SELECT A.CD_NM FROM TB_CMM_CD A WHERE A.CD = E.UPPR_CD), ' > ', E.CD_NM) AS CLSF_NM , A.OPER_FOM_CD , G.CD_NM OPER_FOM_NM , A.EDU_NOPE , A.EDU_HR_MNT , A.RCPT_MTHD_CD , D.CD_NM AS RCPT_MTHD_NM , A.EDU_EXPLN , A.MTTRAT , A.EDU_INTRCN_FILEID , A.EDU_PHOTO_FILEID , A.MON_YN , A.TUES_YN , A.WED_YN , A.THUR_YN , A.FRI_YN , A.SAT_YN , A.SUN_YN , CASE WHEN A.USE_YN = 'Y' THEN '사용' ELSE '미사용' END AS USE_YN ,(SELECT GROUP_CONCAT(TB.CD_NM SEPARATOR',') FROM TB_EDU_MVMN_PRGRM_TRGT_MAPNG TA, TB_CMM_CD TB WHERE TA.PRGRMID = A.PRGRMID and TA.TRGT_CD = TB.CD) AS TRGT_NM , A.MDFCN_DT , A.MDFRID , A.REG_DT , A.RGTRID FROM TB_EDU_MVMN_PRGRM A LEFT OUTER JOIN TB_EDU_SAREA B ON A.SAREAID = B.SAREAID LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_CLSF_MAPNG C ON A.PRGRMID = C.PRGRMID LEFT OUTER JOIN TB_CMM_CD D ON D.CD = A.RCPT_MTHD_CD LEFT OUTER JOIN TB_CMM_CD E ON E.CD = C.CLSF_CD LEFT OUTER JOIN TB_CMM_CD F ON F.CD = A.EDU_SE_CD LEFT OUTER JOIN TB_CMM_CD G ON G.CD = A.OPER_FOM_CD WHERE 1=1
AND A.PRGRM_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.EDU_YR = #{searchEduYr}
AND A.SAREAID = #{searchSareaId}
AND E.UPPR_CD = #{searchClsfCd1}
AND C.CLSF_CD = #{searchClsfCd2}
AND A.RCPT_MTHD_CD = #{searchRcptMthdCd}
AND A.OPER_FOM_CD = #{searchOperFomCd}
AND A.USE_YN = #{searchUseYn}

