mapper.insert:
/* MvmnSchdlDao.insertMvmnSchdl */
INSERT INTO TB_EDU_MVMN_PRGRM_SCHDL (
    YM
    , SCHDL_NM
    , SAREAID
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{ym}
    ,#{schdlNm}
    ,#{sareaId}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* MvmnSchdlDao.updateMvmnSchdl */
UPDATE TB_EDU_MVMN_PRGRM_SCHDL SET
          MDFCN_DT                      = NOW()
        , MDFRID                        = #{user.userid}
 WHERE PRGRM_SCHDLID                    = #{prgrmSchdlid}

mapper.delete:
/* MvmnSchdlDao.deleteMvmnSchdlDe */
DELETE FROM TB_EDU_MVMN_PRGRM_SCHDL_DE
WHERE PRGRM_SCHDLID = #{prgrmSchdlid}

/* MvmnSchdlDao.deleteMvmnSchdlPrgrm */
DELETE FROM TB_EDU_MVMN_PRGRM_SCHDL_PRGRM
WHERE PRGRM_SCHDLID = #{prgrmSchdlid}

/* MvmnSchdlDao.deleteMvmnTmeSchdl */
DELETE FROM TB_EDU_MVMN_PRGRM_TME_SCHDL
WHERE PRGRM_SCHDLID = #{prgrmSchdlid}

/* MvmnSchdlDao.deleteMvmnSchdl */
DELETE FROM TB_EDU_MVMN_PRGRM_SCHDL
WHERE PRGRM_SCHDLID = #{prgrmSchdlid}

mapper.insert:
/* MvmnSchdlDao.insertDeSttId */
INSERT INTO TB_EDU_MVMN_PRGRM_SCHDL_DE(
     DE
     , PRGRM_SCHDLID
     , REG_DT
     , RGTRID
)VALUES

  (
    #{item}
    ,#{prgrmSchdlid}
    ,NOW()
    ,#{user.userid}
   )

/* MvmnSchdlDao.deletePrgrmTmeSchdl */
DELETE FROM TB_EDU_MVMN_PRGRM_TME_SCHDL
WHERE 1=1
  AND PRGRM_SCHDLID = #{prgrmSchdlid}

/* MvmnSchdlDao.deleteDeSttId */
DELETE FROM TB_EDU_MVMN_PRGRM_SCHDL_DE
WHERE 1=1
  AND PRGRM_SCHDLID = #{prgrmSchdlid}

/* MvmnSchdlDao.deletePrgrmSttId */
DELETE FROM TB_EDU_MVMN_PRGRM_SCHDL_PRGRM
WHERE 1=1
  AND PRGRM_SCHDLID = #{prgrmSchdlid}

mapper.select:
/* MvmnSchdlDao.selectMvmnAplyCnt */
SELECT COUNT(A.APLYID)
FROM TB_EDU_MVMN_PRGRM_APLY A
LEFT OUTER JOIN TB_EDU_MVMN_PRGRM_APLY_SCHDL B ON A.APLYID = B.APLYID
WHERE B.TME_SCHDLID IN (
    SELECT TME_SCHDLID
      FROM TB_EDU_MVMN_PRGRM_TME_SCHDL
     WHERE 1=1
       AND PRGRM_SCHDLID = #{prgrmSchdlid}
)
