mapper.select:
/* PrgrmgdDao.selectPrgrmgdList */
SELECT A.PRGRMID , A.CLSF_CD , A.RGN_CD , D.CD_NM AS RGNCDNM , CONCAT(A.YR, '-' ,A.MM) AS YRMM , A.YR , A.MM , A.TTL , A.DT , A.URL , A.THMBN_FILEID , C.FILEGRPID , A.CN_STYLE , A.CN , A.MDFCN_DT , A.MDFRID , A.REG_DT AS REGDT , A.RGTRID , B.NM FROM TB_CMM_WBZN_PRGRM A LEFT OUTER JOIN TB_CMM_USER B ON A.RGTRID = B.USERID LEFT OUTER JOIN TB_CMM_FILE C ON A.THMBN_FILEID = C.FILEID LEFT OUTER JOIN TB_CMM_CD D ON A.RGN_CD = D.CD WHERE 1=1
AND A.CLSF_CD = #{clsfCd}
AND A.TTL LIKE CONCAT('%',#{searchKeyword},'%')
AND A.RGN_CD = #{searchRgnCd}
AND A.YR = #{searchYr}
AND A.MM = #{searchMm}

mapper.insert:
/* PrgrmgdDao.insertPrgrmgd */
INSERT INTO TB_CMM_WBZN_PRGRM (
    CLSF_CD
    , RGN_CD
    , YR
    , MM
    , TTL
    , DT

    , URL

    , THMBN_FILEID
    , TYPE_CD

    , CN_STYLE


    , CN

    , CN_SUMRY
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
)VALUES(
    #{clsfCd}
    ,#{rgnCd}
    ,#{yr}
    ,#{mm}
    ,#{ttl}
    ,#{dt}

    ,#{url}

    ,#{thmbnFileid}
    ,#{typeCd}

    ,#{cnStyle}


    ,#{cn}

    ,#{cnSumry}
    ,NOW()
    ,#{user.userid}
    ,NOW()
    ,#{user.userid}
)

mapper.update:
/* PrgrmgdDao.updatePrgrmgd */
UPDATE TB_CMM_WBZN_PRGRM SET
        RGN_CD              = #{rgnCd}
        , YR                     = #{yr} 
        , MM                     = #{mm} 
        , TTL                    = #{ttl}
        , DT                     = #{dt}
        , THMBN_FILEID           = #{thmbnFileid}
        , TYPE_CD                = #{typeCd}

        , url                    = #{url}


        , CN_STYLE               = #{cnStyle}


        , CN                     = #{cn}

        , CN_SUMRY               = #{cnSumry}
        , MDFCN_DT               = NOW() 
        , MDFRID                 = #{user.userid}
 WHERE PRGRMID = #{prgrmid}
