mapper.insert:
/* QestnrDao.insertQestnr */
INSERT INTO TB_CMM_QESTNR (
    QESTNRID
    , SITEID
    , QESTNR_KND_CD
    , QESTNR_NM
    , EXPLN
    , USE_YN
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
) VALUES (
    #{qestnrid}
    , #{siteid}
    , #{qestnrKndCd}
    , #{qestnrNm}
    , #{expln}
    , #{useYn}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.select:
/* QestnrDao.selectQestnrList */
SELECT A.QESTNRID , A.SITEID , A.QESTNR_KND_CD , A.QESTNR_NM , A.EXPLN , A.USE_YN , A.REG_DT , COUNT(B.QESTNRID) AS QITEM_CNT FROM TB_CMM_QESTNR A LEFT OUTER JOIN TB_CMM_QESTNR_QITEM B ON (B.QESTNRID = A.QESTNRID) WHERE 1 = 1
AND SITEID = #{searchSiteid}
AND QESTNR_KND_CD = #{searchQestnrKndCd}
GROUP BY A.QESTNRID

mapper.update:
/* QestnrDao.updateQestnr */
UPDATE TB_CMM_QESTNR SET
    SITEID = #{siteid}
    , QESTNR_KND_CD = #{qestnrKndCd}
    , QESTNR_NM = #{qestnrNm}
    , EXPLN = #{expln}
    , USE_YN = #{useYn}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE QESTNRID = #{qestnrid}

mapper.insert:
/* QestnrDao.insertQestnr */
INSERT INTO TB_CMM_QESTNR_QITEM (
    QITEMID
