    , ORDR
    , USE_YN
    , MDFCN_DT
    , MDFRID
    , REG_DT
    , RGTRID
) VALUES (
    #{qitemid}
    , #{qestnrid}
    , #{qitemTypeCd}
    , #{cn}
    , #{exCnt}
    , #{scale}
    , #{ordr}
    , #{useYn}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

/* QestnrDao.insertQitemEx */
