    #{exid}
    , #{qitemid}
    , #{exNo}
    , #{cn}
    , #{scr}
    , #{etcYn}
    , NOW()
    , #{user.userid}
    , NOW()
    , #{user.userid}
)

mapper.update:
/* QestnrDao.updateQitemOrdr */
UPDATE TB_CMM_QESTNR_QITEM SET
    ORDR = #{ordr}
    , MDFCN_DT = NOW()
    , MDFRID = #{user.userid}
WHERE QITEMID = #{qitemid}

mapper.delete:
/* QestnrDao.deleteQitemOrdr */
DELETE 
    FROM TB_CMM_QESTNR_QITEM
WHERE QITEMID IN

    #{item}

/* QestnrDao.deleteQitemEx */
DELETE 
    FROM TB_CMM_QESTNR_QITEM_EX
WHERE QITEMID IN

    #{item}
