mapper.select:
/* RcpmnyBfeDao.selectRcpmnyBfeList */
SELECT A.APLYID ,A.APLY_DT ,A.STLM_STTS_CD ,A.APLY_STTS_CD ,A.APLCNTID ,A.APLCNT_NM ,A.AMT ,A.DPST_BANK_CD ,A.DPST_AMT ,A.RFND_DMND_AMT ,A.RFND_AMT ,A.RFND_REJECT_RSN_CD ,A.MDFCN_DT ,A.DPST_IDNTY_DT ,C.INSTID ,C.INST_NM ,B.BGNG_DT ,B.END_DT ,B.ALLDAY_YN ,E.SPCE_NM ,F.FCLT_NM ,G.ACNT ,G.NM ,A.REG_DT FROM TB_FCL_SPCE_APLY A INNER JOIN (SELECT B.APLYID, D.SPCEID, MIN(D.BGNG_DT) AS BGNG_DT, B.ALLDAY_YN, CASE WHEN B.ALLDAY_YN = 'N' THEN MAX(D.END_DT) ELSE MAX(D.BGNG_DT) END AS END_DT FROM TB_FCL_APLY_RSVTDE B LEFT JOIN TB_FCL_SPCE_RSVTDE D ON B.RSVTDEID = D.RSVTDEID GROUP BY APLYID) B ON A.APLYID = B.APLYID INNER JOIN TB_FCL_SPCE E ON B.SPCEID = E.SPCEID INNER JOIN TB_FCL_FCLT F ON E.FCLTID = F.FCLTID INNER JOIN TB_CMM_INST C ON F.INSTID = C.INSTID LEFT OUTER JOIN TB_CMM_USER G ON A.APLCNTID = G.USERID WHERE 1=1 AND APLY_STTS_CD IN ('160101' ,'160103') AND STLM_STTS_CD = '161101'
AND E.SPCE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND F.FCLT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND C.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND G.ACNT LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLCNT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLY_STTS_CD = #{searchAplySttsCd}
AND A.STLM_STTS_CD = #{searchStlmSttsCd}
AND B.BGNG_DT >= date(#{searchBgngDt})
AND B.END_DT <= date(#{searchEndDt})
AND A.APLYID = #{selectedApplyid}
AND C.INSTID = #{user.instid}

mapper.update:
/* RcpmnyBfeDao.updateDsptCheck */
UPDATE TB_FCL_SPCE_APLY SET
     DPST_DE              = #{dpstDe}
     ,APLY_STTS_CD        = '160102'
     ,STLM_STTS_CD        = '161102'
     ,DPST_AMT            = #{dpstAmt}
     ,DPST_IDNTY_DT       = NOW()
     ,CNCL_RSN_CD         = null
     ,RSVT_REJECT_RSN     = null
     ,RFND_REJECT_RSN_CD  = null
     ,RFND_REJECT_RSN     = null
     ,MDFCN_DT            = NOW()
     ,MDFRID              = #{user.userid}
WHERE APLYID = #{aplyid}

/* RcpmnyBfeDao.updateResveCancel */
UPDATE TB_FCL_SPCE_APLY SET
     CNCL_RSN_CD         = #{cnclRsnCd}
     ,APLY_STTS_CD       = '160103'
     ,RSVT_CNCL_RSN      = #{rsvtCnclRsn}
     ,CNCL_DT            = NOW()
     ,RFND_REJECT_RSN_CD = null 
     ,RFND_REJECT_RSN    = null
     ,MDFRID             = #{user.userid}
     ,MDFCN_DT           = NOW()
WHERE 1=1

   AND APLYID = #{aplyid}


  AND APLYID IN (
     SELECT APLYID FROM TB_FCL_SPCE_APLY WHERE APLY_STTS_CD != '160103'  AND APLYID IN 
     (

        #{aplyid}

      )
    )
