mapper.select:
/* RefndMngDao.selectRefndMngList */
SELECT A.APLYID ,A.STLM_STTS_CD ,A.APLY_STTS_CD ,A.APLCNTID ,A.APLCNT_NM ,A.AMT ,A.DPST_AMT ,A.RFND_DMND_AMT ,A.RFND_AMT ,A.MDFCN_DT ,A.REG_DT ,A1.CD_NM AS APLY_STTS_CD_NM ,A2.CD_NM AS STLM_STTS_CD_NM ,C.INSTID ,B.ALLDAY_YN ,B.BGNG_DT ,B.END_DT ,E.SPCE_NM ,F.FCLT_NM ,G.ACNT ,G.NM FROM TB_FCL_SPCE_APLY A INNER JOIN ( SELECT CD , CD_NM FROM TB_CMM_CD WHERE CDGRPID = 160 ) A1 ON (A.APLY_STTS_CD = A1.CD) INNER JOIN ( SELECT CD , CD_NM FROM TB_CMM_CD WHERE CDGRPID = 161 ) A2 ON (A.STLM_STTS_CD = A2.CD) INNER JOIN (SELECT B.APLYID, D.SPCEID, MIN(D.BGNG_DT) AS BGNG_DT, B.ALLDAY_YN, CASE WHEN B.ALLDAY_YN = 'N' THEN MAX(D.END_DT) ELSE MAX(D.BGNG_DT) END AS END_DT FROM TB_FCL_APLY_RSVTDE B LEFT JOIN TB_FCL_SPCE_RSVTDE D ON B.RSVTDEID = D.RSVTDEID GROUP BY APLYID) B ON A.APLYID = B.APLYID INNER JOIN TB_FCL_SPCE E ON B.SPCEID = E.SPCEID INNER JOIN TB_FCL_FCLT F ON E.FCLTID = F.FCLTID INNER JOIN TB_CMM_INST C ON F.INSTID = C.INSTID LEFT OUTER JOIN TB_CMM_USER G ON A.APLCNTID = G.USERID WHERE 1=1
AND STLM_STTS_CD IN(#{stlmSttsCd})
AND E.SPCE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND F.FCLT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND C.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND G.ACNT LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLCNT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLY_STTS_CD = #{searchAplySttsCd}
AND A.STLM_STTS_CD = #{searchStlmSttsCd}
AND B.BGNG_DT >= date(#{searchBgngDt})
AND B.END_DT <= date(#{searchEndDt})
AND A.APLYID = #{selectedApplyid}

mapper.update:
/* RefndMngDao.updateRefndComplete */
UPDATE TB_FCL_SPCE_APLY SET
    RFND_DE                = #{rfndDe}
     ,RFND_AMT                    = #{rfndAmt}
     ,APLY_STTS_CD                 = '160103'
     ,STLM_STTS_CD                 = '161104'
     ,RFND_BANK_CD                    = #{rfndBankCd}
     ,RFND_BACNT                    = #{rfndBacnt}
     ,RFND_PYR_NM                    = #{rfndPyrNm}
     ,MDFCN_DT                 = NOW()
     ,MDFRID                 = #{user.userid}
WHERE APLYID = #{aplyid}

/* RefndMngDao.updateResveCancel */
UPDATE TB_FCL_SPCE_APLY SET
     CNCL_RSN_CD                    = #{cnclRsnCd}
     ,APLY_STTS_CD                    = '160102'
     ,STLM_STTS_CD                    = '161102'
     ,RFND_AMT                    = 0
     ,RFND_REJECT_RSN                    = #{rfndRejectRsn}
     ,CNCL_DT                 = NOW()
     ,MDFRID                 = #{user.userid}
     ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}

/* RefndMngDao.updateRefndRollback */
UPDATE TB_FCL_SPCE_APLY SET
     CNCL_RSN_CD                    = ''
     ,STLM_STTS_CD                    = '161103'
     ,RFND_REJECT_RSN                    = ''
     ,CNCL_DT                 = null
     ,MDFRID                 = #{user.userid}
     ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}
