mapper.select:
/* RefndMngDao.selectRefndMngList */
SELECT A.APLYID ,A.APLY_DT ,A.STLM_STTS_CD ,A.APLY_STTS_CD ,A.APLCNTID ,A.APLCNT_NM ,A.AMT ,A.DPST_BANK_CD ,A.DPST_AMT ,A.RFND_DMND_AMT ,A.RFND_AMT ,A.RFND_REJECT_RSN_CD ,A.MDFCN_DT ,A.DPST_IDNTY_DT ,C.INSTID ,C.INST_NM ,B.BGNG_DT ,B.END_DT ,B.ALLDAY_YN ,E.SPCE_NM ,F.FCLT_NM ,G.ACNT ,G.NM ,A.REG_DT FROM TB_FCL_SPCE_APLY A INNER JOIN (SELECT B.APLYID, D.SPCEID, MIN(D.BGNG_DT) AS BGNG_DT, B.ALLDAY_YN, CASE WHEN B.ALLDAY_YN = 'N' THEN MAX(D.END_DT) ELSE MAX(D.BGNG_DT) END AS END_DT FROM TB_FCL_APLY_RSVTDE B LEFT JOIN TB_FCL_SPCE_RSVTDE D ON B.RSVTDEID = D.RSVTDEID GROUP BY APLYID) B ON A.APLYID = B.APLYID INNER JOIN TB_FCL_SPCE E ON B.SPCEID = E.SPCEID INNER JOIN TB_FCL_FCLT F ON E.FCLTID = F.FCLTID INNER JOIN TB_CMM_INST C ON F.INSTID = C.INSTID LEFT OUTER JOIN TB_CMM_USER G ON A.APLCNTID = G.USERID WHERE 1=1
AND STLM_STTS_CD IN(#{stlmSttsCd})
AND E.SPCE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND F.FCLT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND C.INST_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND G.ACNT LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLCNT_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND A.APLY_STTS_CD = #{searchAplySttsCd}
AND A.STLM_STTS_CD = #{searchStlmSttsCd}
AND B.BGNG_DT >= date(#{searchBgngDt})
AND B.END_DT <= date(#{searchEndDt})
AND A.APLYID = #{selectedApplyid}

mapper.update:
/* RefndMngDao.updateRefndComplete */
UPDATE TB_FCL_SPCE_APLY SET
    RFND_DE                = #{rfndDe}
     ,RFND_AMT                    = #{rfndAmt}
     ,APLY_STTS_CD                 = '160103'
     ,STLM_STTS_CD                 = '161104'
     ,RFND_BANK_CD                    = #{rfndBankCd}
     ,RFND_BACNT                    = #{rfndBacnt}
     ,RFND_PYR_NM                    = #{rfndPyrNm}
     ,MDFCN_DT                 = NOW()
     ,MDFRID                 = #{user.userid}
WHERE APLYID = #{aplyid}

/* RefndMngDao.updateResveCancel */ UPDATE TB_FCL_SPCE_APLY A LEFT OUTER JOIN ( SELECT A.APLYID ,COUNT(*) AS CNT FROM TB_FCL_APLY_RSVTDE A INNER JOIN TB_FCL_SPCE_APLY B ON A.APLYID = B.APLYID INNER JOIN tb_fcl_spce_rsvtde C ON A.RSVTDEID = C.RSVTDEID WHERE 1=1 AND B.APLY_STTS_CD IN ('160101','160102') AND A.APLYID NOT IN (
#{aplyid}
) AND A.RSVTDEID IN( SELECT A.RSVTDEID FROM TB_FCL_APLY_RSVTDE A WHERE APLYID IN (
#{aplyid}
) ) GROUP BY A.APLYID ) B ON A.APLYID = B.APLYID SET APLY_STTS_CD = CASE WHEN B.CNT > 0 THEN '160103' ELSE '160102' END ,STLM_STTS_CD = '161102' ,DPST_IDNTY_DT = CASE WHEN B.CNT > 0 THEN NULL ELSE NOW() END ,CNCL_DT = CASE WHEN B.CNT > 0 THEN NOW() ELSE NULL END ,CNCL_RSN_CD = CASE WHEN B.CNT > 0 THEN '243101' ELSE NULL END ,RFND_DMND_DT = NULL ,RFND_DE = NULL ,RFND_AMT = null ,RFND_DMND_AMT = NULL ,RFND_CMPTN_DT = NULL ,MDFRID = #{user.userid} ,MDFCN_DT = NOW() WHERE A.APLYID IN (
#{aplyid}
)

/* RefndMngDao.updateRefndRollback */
UPDATE TB_FCL_SPCE_APLY SET
     CNCL_RSN_CD                    = ''
     ,STLM_STTS_CD                    = '161103'
     ,RFND_REJECT_RSN                    = ''
     ,CNCL_DT                 = null
     ,MDFRID                 = #{user.userid}
     ,MDFCN_DT                 = NOW()
WHERE APLYID = #{aplyid}
