mapper.select:
/* SiteDao.selectSiteList */
SELECT SITEID, SITE_NM, SYS_SE_CD, USE_YN, MDFCN_DT, (SELECT GROUP_CONCAT(DMN) FROM TB_CMM_SITE_DMN WHERE SITEID = S.SITEID) AS DOMAINS FROM TB_CMM_SITE S WHERE 1=1
AND SYS_SE_CD = #{searchType}
AND SITE_NM LIKE CONCAT('%', #{searchKeyword}, '%')

mapper.update:
/* SiteDao.updateSiteDomain */
UPDATE 
    TB_CMM_SITE_DMN
SET
    DMN = #{dmn},
    DMN_DC = #{dmnDc},
    MDFCN_DT = NOW(),
    MDFRID = #{mdfrid}
WHERE
    SITEID = #{siteid}
    AND DMN = #{dmnOrg}
