mapper.insert:
INSERT INTO TB_SPB_JDGS_GRP(
        GRP_NM
        , USE_YN
        , MDFCN_DT
        , MDFRID
        , REG_DT
        , RGTRID
    ) VALUES (
        #{grpNm}
        , #{useYn}
        , NOW()
        , #{user.userid}
        , NOW()
        , #{user.userid}
       )

mapper.update:
UPDATE
    TB_SPB_JDGS_GRP
SET
    USE_YN = #{useYn}
  , GRP_NM = #{grpNm}
  , MDFCN_DT = NOW()
  , MDFRID = #{user.userid}
WHERE
    GRPID = #{grpId}

mapper.delete:
DELETE
FROM TB_SPB_JDGS_GRP_MAPNG
WHERE GRPID = #{grpId}
  AND USERID = #{userid}

mapper.select:
SELECT USERID
FROM TB_SPB_JDGS_GRP_MAPNG
WHERE GRPID = #{grpId}
